INSERT INTO `bug` VALUES (396, 395, 11, 'Windows-确定订单无效', 'codeerror', '2', '<p>[步骤]1、swmeky登录系统新增运单，选择委托方chuanhhc发布订单</p>\n<p>2、登录chuanhch账号在【我的订单】-【待接单】-【订单详情】页面点击确定</p>\n<p>[结果]系统提示操作成功，订单状态没有改变</p>\n<p>[期望]订单状态会跟着改变</p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-11-27 11:37:35', 'closed', 'changguocheng', '2019-03-26 16:03:25', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (397, 396, 11, 'Windows-新增人员无法审批', 'codeerror', '2', '<p>[步骤]1、登录jinyi账号新增人员信息，新增人员状态为待审批</p>\n<p>2、登录whpadmin账号</p>\n<p>[结果]whpadmin账号没有审批人员模块</p>\n<p>[期望]whpadmin可以审批人员</p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-11-27 13:09:33', 'closed', 'changguocheng', '2018-12-06 15:36:44', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (398, 397, 11, 'iOS-小程序接受任务后没有提示而且自动跳转进入提货页面', 'codeerror', '3', '<p>[步骤]1、委托方四必查通过后，小程序生成新的任务</p>\n<p>2、司机接受任务成功</p>\n<p>[结果]小程序没有提示操作成功，而且自动跳转进入提货页面</p>\n<p>[期望]小程序有提示操作成功，不会自动跳转进入提货页面</p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-11-27 13:59:15', 'closed', 'changguocheng', '2019-03-19 09:47:50', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (399, 398, 11, 'Windows-查询条件字段名称不统一', 'codeerror', '3', '<p>[步骤]登录jinyi账号进入【订单管理】-【我的订单】</p>\n<p>[结果]查询条件和列表标题字段名称不统一</p>\n<p>[期望]<span>查询条件和列表标题字段名称统一</span></p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-11-27 14:10:41', 'closed', 'zhangbin', '2019-03-26 09:57:57', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (400, 399, 11, 'Windows-备注超过140个字符也能保存成功', 'codeerror', '2', '<p>[步骤]1、登录chuanhhc账号发布订单，其他内容填写正确，备注填写超过140个字符</p>\n<p>2、点击下一步</p>\n<p>[结果]可以成功进入下一步界面</p>\n<p>[期望]备注提示填写不能超过140个字符，当超过140个字符时应该不能进行下一步</p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-11-27 14:22:41', 'closed', 'changguocheng', '2018-12-26 13:39:03', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (401, 400, 11, 'Windows-企业信息对齐方式不统一', 'codeerror', '3', '<p>[步骤]登录chuanhhc点击【企业管理】-【企业信息】，查看企业信息</p>\n<p>[结果]企业信息对齐方式不统一</p>\n<p>[期望]<span>建议左对齐</span></p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-11-27 14:35:31', 'closed', 'changguocheng', '2018-12-14 11:03:19', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (402, 401, 11, 'Windows-驳回运单没有归类到有争议运单', 'codeerror', '3', '<p>[步骤]登录jinyi账号查看对账信息，默认显示全部信息，点击查看有争议运单</p>\n<p>[结果]驳回运单没有归类到有争议运单</p>\n<p>[期望]<span>驳回运单应该归类到有争议运单</span></p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-11-27 14:47:16', 'closed', 'changguocheng', '2018-12-25 15:33:37', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (403, 402, 13, 'Android-历史消息时间显示错误', 'codeerror', '2', '<p>[步骤]1、登录系统管理员账号给系统测试账号发消息“这”，消息发送成功之后，点击返回到消息页面</p>\n<p>2、返回到消息页面成功，再点击查看系统测试账号历史信息</p>\n<p>[结果]时间显示错误</p>\n<p>[期望]时间显示正确</p>\n<p>[环境]MI5s Android6.0.1</p>', 'closed', 'changguocheng', '2018-11-27 16:50:18', 'closed', 'changguocheng', '2019-01-03 10:42:40', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (404, 403, 11, 'Windows-系统地图和需求地图不一致', 'codeerror', '2', '<p>[步骤]登录jinyi账号点击进入【运单管理】-【我的运单】查看货物跟踪</p>\n<p>[结果]使用的地图为高德地图，需求地图为百度地图</p>\n<p>[期望]系统和需求应该一致</p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-11-28 09:45:45', 'closed', 'changguocheng', '2018-12-24 15:33:40', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (405, 404, 13, 'Android-新建消息页面【添加图片】按钮功能未实现', 'codeerror', '3', '<p>[步骤]</p>\n<p>1.登录APP，点主页-家校通-教师版-消息通知进入新建消息页面</p>\n<p>2.新建消息，点【添加图片】按钮</p>\n<p>[结果]</p>\n点【添加图片】按钮没有响应<br /><p>[期望]</p>\n<p>可以正常添加图片</p>\n<p>[环境]</p>\n<p>魅蓝&nbsp; Note6&nbsp; 7.1.2</p>\n<br />', 'closed', 'wuhaili', '2018-11-28 11:05:15', 'closed', 'admin', '2019-02-28 15:05:33', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (406, 405, 13, 'Android-家校通-教师版页面点各个小模块页面跳转不正常', 'codeerror', '3', '<p>[步骤]</p>\n<p>1.登录APP，点-家校通-教师版进入页面</p>\n<p>2.点“消息通知”、“班级花名册”等各个小模块</p>\n<p>[结果]</p>\n页面先是跳转到一张背景图，需要点一下背景图或点手机的返回键才能进入小模块的正常页面<br /><p>[期望]</p>\n<p>在家校通教师版页面点各个小模块应该直接跳转到对应的页面</p>\n<p>[环境]</p>\n<p>魅蓝 Note6&nbsp; 7.1.2</p>', 'closed', 'wuhaili', '2018-11-28 11:30:14', 'closed', 'admin', '2019-02-28 15:05:33', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (407, 406, 13, 'Android-实时位置共享不能定位', 'codeerror', '2', '<p>[步骤]1、登录系统管理员账号</p>\n<p>2、进入消息模块，点击进入发送消息页面</p>\n<p>3、给系统测试账号发送实时位置共享</p>\n<p>[结果]无法给自己定位</p>\n<p>[期望]可以给自己定位</p>\n<p>[环境]MI5s Android6.0.1</p>', 'closed', 'changguocheng', '2018-11-28 13:13:27', 'closed', 'changguocheng', '2018-12-24 11:10:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (408, 407, 13, 'Android-无法进入待办事项页面且提示错误', 'codeerror', '3', '<p>\n	[步骤]1、登录系统管理员账号\n</p>\n<p>\n	2、点击进入通讯录，点击服务号，点击选择查看待办事项\n</p>\n<p>\n	[结果]系统提示当前版本不支持打开，请升级到最新版本再重试后又提示打开出错，请检查服务号配置，而且无法进入待办事项页面\n</p>\n<p>\n	[期望]无法进入页面时应该提示一个问题，需求功能应该正常\n</p>\n<p>\n	[环境]MI5s Android6.0.1\n</p>', 'resolved', 'changguocheng', '2018-11-28 13:24:15', 'zhangjch', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (409, 408, 13, 'Android-发送消息页面无法点击空白收回输入法', 'codeerror', '3', '<p>[步骤]1、登录系统管理员账号发送消息给系统测试账号信息</p>\n<p>2、点击输入框，调出输入法</p>\n<p>3、点击空白处</p>\n<p>[结果]输入法无法收回</p>\n<p>[期望]建议可以通过点击空白出收回输入发</p>\n<p>[环境]MI5s Android6.0.1</p>', 'closed', 'changguocheng', '2018-11-28 13:29:53', 'closed', 'zhangjch', '2019-04-19 10:46:07', NULL, NULL, '0', 'zt');
