INSERT INTO `bug` VALUES (411, 410, 13, 'Android-web端无法接收系统管理员账号通过文件传输助手发送的消息', 'codeerror', '3', '<p>\n	[步骤]登录系统管理员账号通过文件传输助手发送消息到web端\n</p>\n<p>\n	[结果]app端显示发送成功，在web端查看消息时没有发送的消息\n</p>\n<p>\n	[期望]发送消息成功时，web端可以成功接收到\n</p>\n<p>\n	[环境]MI5s Android6.0.1\n</p>', 'active', 'changguocheng', '2018-11-28 14:02:30', 'pingaoy', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (412, 411, 13, 'Android-群签到信息在聊天页面不显示', 'codeerror', '3', '<p>[步骤]登录系统管理员账号在系统管理员、网络管理员、管理员群签到</p>\n<p>[结果]页面提示签到成功，聊天页面没有显示签到信息，在群内签到页面可以查看到签到信息</p>\n<p>[期望]聊天页面也应显示签到信息</p>\n<p>[环境]MI5s Android6.0.1</p>', 'active', 'changguocheng', '2018-11-28 14:18:05', 'pingaoy', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (413, 412, 13, 'Android-评价录入页面输入评价，点保存后页面应该自动刷新一下', 'codeerror', '3', '<p>[步骤]</p>\n<p>1.登录APP，点主页-家校通-教师版-评价录入-选择班级-选择学生-选择时间，进入评价录入界面</p>\n<p>2.输入评价，点保存</p>\n<p>[结果]</p>\n提示操作成功且一直停留在操作成功页面<br /><p>[期望]</p>\n<p>保存评论成功后页面应该自动刷新一下</p>\n<p>[环境]</p>\n<p>魅蓝 Note6&nbsp; 7.1.2</p>', 'closed', 'wuhaili', '2018-11-28 14:18:20', 'closed', 'admin', '2019-02-28 15:05:34', NULL, NULL, '0', 'zt');
