INSERT INTO `bug` VALUES (449, 448, 11, '【企业信息管理-车辆管理信息】新增车辆管理信息显示在列表最后一行', 'codeerror', '3', '<p>[步骤]登录危险废物监控系统，点击车辆管理新增按钮，正确填写新增内容，点击保存，系统提示新增成功</p>\n<p>[结果]新增车辆管理信息显示在列表最后一行</p>\n<p>修改车辆信息也显示在列表的最后一行</p>\n<p>[期望]新增的车辆管理信息显示在列表的第一行</p>\n<p>[环境]win7+Chrome56+分辨率1366*768</p>', 'closed', 'zhongle', '2018-12-03 10:03:42', 'closed', 'zhongle', '2019-02-15 16:27:59', NULL, NULL, '0', 'zt');
