INSERT INTO `bug` VALUES (503, 502, 16, '班牌控制台-分组管理-设备-添加设备', 'codeerror', '1', '<p>[步骤]</p>\n在添加设备中，选择一个设备名称并勾选，点击添加，<br /><p>[结果]</p>\n<span>以前的设备分组中的记录没有删除，</span><span>也导致班牌的统计中展示的班牌数量不对</span><span></span><br /><p>[期望]</p>\n<p>建议删除一个设备，才能移动这个设备</p>', 'closed', 'zhangjch', '2018-12-07 10:53:41', 'closed', 'zhangjch', '2018-12-07 10:54:30', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (504, 503, 16, '班牌控制台-分组管理-新增-多次点击立即创建按钮', 'codeerror', '3', '<p>[步骤]</p>\n击新增按钮，输入分组名称，选择模板类型，多次点击立即创建，<br /><p>[结果]</p>\n<span>提示缺少分组名称，新增成功</span>，提示不太友好<br /><p>[期望]</p>\n<p>只提示新增成功</p>', 'closed', 'zhangjch', '2018-12-07 10:56:44', 'closed', 'zhangjch', '2018-12-07 10:57:15', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (505, 504, 16, '智能分析-班牌统计-数据不准确', 'codeerror', '2', '<p>[步骤]</p>\n查看班牌统计数据<br /><p>[结果]</p>\n1.全部的班牌与设备管理的设备数量不匹配。<br />\n2.全部的班牌数量！=欢迎班牌+办公室班牌+教学班牌的数量<br /><p>[期望]</p>\n<p><span>全部的班牌数量=欢迎班牌+办公室班牌+教学班牌等类型的班牌数量的总和</span></p>\n<br />', 'closed', 'zhangjch', '2018-12-07 10:58:54', 'closed', 'zhangjch', '2018-12-07 11:00:01', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (506, 505, 16, '智能分析-班牌统计-班牌使用状况-百分比率值太大，被遮盖', 'interface', '3', '<p>[步骤]</p>\n查看班牌使用状况界面显示<br /><p>[结果]</p>\n<span>百分比率值太大，被遮盖，建议改小点</span><span></span><br /><p>[期望]</p>\n百分比<span>率值不要被遮盖</span>', 'closed', 'zhangjch', '2018-12-07 11:01:19', 'closed', 'zhangjch', '2018-12-07 11:01:55', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (507, 506, 16, '班牌设计-校园通知-新增-功能不全', 'codeerror', '2', '<p>[步骤]</p>\n查询条件通知状态有两种:已发送、草稿两种状态，<br /><p>[结果]</p>\n<span>而保存中只有发送按钮，缺少保存按钮</span><span></span><br /><p>[期望]</p>\n新增一个保存草稿状态的功能', 'closed', 'zhangjch', '2018-12-07 11:05:05', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (508, 507, 16, '班牌设计-校园通知-发送情况-显示不正确', 'codeerror', '3', '<p>[步骤]</p>\n新增一条校园通知，点击发送，<br /><p>[结果]</p>\n<span>列表上显示已发数为0，在终端上已读此通知，列表上显示已读为0</span><span></span><br /><p>[期望]</p>\n<p>正确显示已读通知的人数</p>', 'closed', 'zhangjch', '2018-12-07 11:11:32', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (509, 508, 16, '班牌设计-校园通知-详情-阅读记录显示不正确', 'codeerror', '2', '<p>[步骤]</p>\n查看阅读记录显示情况<br /><p>[结果]</p>\n显示已读人数显示为0、未读人数为0<br /><p>[期望]</p>\n<p>读了通知，已读人数增加，未读人数减少</p>', 'closed', 'zhangjch', '2018-12-07 11:15:08', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (510, 509, 16, '班牌设计-校园通知-新增-发送范围', 'codeerror', '2', '<p>[步骤]</p>\n点击发送范围，<br /><p>[结果]</p>\n发送范围为按单个年级和班级发送，<br /><p>[期望]</p>\n<span>希望支持多个年级发送或者多个班级发送。</span><span></span><br />', 'closed', 'zhangjch', '2018-12-07 11:17:02', 'closed', 'zhangjch', '2018-12-07 11:21:01', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (511, 510, 16, '班牌设计-文明之星-新增(图片和内容)-班牌上显示排版太乱且没有标题显示', 'interface', '2', '<p>[步骤]</p>\n新增一个文明之星记录（包括图片+内容）,发送，在班牌上查看文明之星，<br /><p>[结果]</p>\n<span>显示：图片和内容排版不美观。且缺少标题内容</span><br /><p>[期望]</p>\n显示文明之星标题，图片和内容排版美观', 'closed', 'zhangjch', '2018-12-07 11:23:35', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (512, 511, 16, '班牌设计-文明之星-发布-响应时间太长', 'codeerror', '2', '<p>[步骤]</p>\n发布文明之星，查看文明之星的更新时间<br /><p>[结果]</p>\n<span>班牌更新时间较长</span><br /><p>[期望]</p>\n班牌能够快速响应更新信息<span></span>', 'closed', 'zhangjch', '2018-12-07 11:26:20', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (513, 512, 16, '门户系统后台-信息管理-信息发布-新建', 'codeerror', '3', '<p>[步骤]</p>\n新建一个文章，点击保存，保存成功，界面上没有显示新增的文章记录，只有点击查询后才会显示。应该为：1.保存后，关闭信息内容新增框。2.新增信息显示在列表上<br /><p>[结果]</p>\n<br /><p>[期望]</p>\n<br />', 'closed', 'dengyl', '2018-12-07 11:26:59', 'closed', 'dengyl', '2018-12-07 13:10:48', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (514, 513, 16, '班牌设计-文明之星-撤下-响应时间太长', 'codeerror', '2', '<p>[步骤]</p>\n撤下文明之星主题，查看班牌的响应时间<br /><p>[结果]</p>\n<span>响应时间也比较长</span><span></span><br /><p>[期望]</p>\n希望能够及时响应更新信息', 'closed', 'zhangjch', '2018-12-07 11:28:16', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (515, 514, 16, '门户系统后台-信息管理-信息发布-审核-弹框太乱', 'codeerror', '3', '<p>[步骤]</p>\n点击审核，弹出信息内容审核框，选择通过或者不通过，点击确认，有3个问题：1.点击确认后，有两个弹框内容跳转。截图见备注（o列）。2.点击确认，内容审核框不会关闭。3.审核结束后，被审核文章的状态不会改变，只能通过查询，才会改变。<br /><p>[结果]</p>\n<br /><p>[期望]</p>\n<br />', 'closed', 'dengyl', '2018-12-07 11:29:33', 'closed', 'dengyl', '2018-12-07 13:10:41', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (516, 515, 16, '班牌设计-文明之星-编辑-界面有时显示不完全（有时候出现）', 'codeerror', '3', '<p>[步骤]</p>\n编辑文明之星，查看文明之星的详情<br /><p>[结果]</p>\n界面有时不显示详情的的内容<br /><p>[期望]</p>\n每次都能显示详情的内容', 'closed', 'zhangjch', '2018-12-07 11:30:13', 'closed', 'zhangjch', '2018-12-07 11:30:43', NULL, NULL, '0', 'zt');
