INSERT INTO `bug` VALUES (521, 519, 16, '班牌设计-文明之星-编辑-范围', 'codeerror', '2', '<p>[步骤]</p>\n选择一条文明之星记录，点击编辑<br /><p>[结果]</p>\n编辑字段不可修改<br /><p>[期望]</p>\n建议范围也可以修改<br />', 'closed', 'zhangjch', '2018-12-07 12:49:13', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
