INSERT INTO `bug` VALUES (527, 526, 16, '班级管理-班级列表-编辑-没有返回班级列表界面', 'interface', '2', '<p>[步骤]</p>\n点击编辑，修改内容，点击保存，<br /><p>[结果]</p>\n<p>保存成功，界面还处于编辑页面</p>\n<p>[期望]</p>\n点击保存按钮后，保存成功，页面从编辑界面跳到到班级列表页面', 'closed', 'zhangjch', '2018-12-07 13:09:01', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (528, 527, 16, '班级管理-班级列表-编辑-选择班主任和选择副班主任的老师列表个数不同', 'codeerror', '2', '<p>[步骤]</p>\n选择班主任，老师列表显示为3个（截图1）。选择副班主任，老师列表中显示4个，其中有一个为无（截图2）。<br /><p>[结果]</p>\n老师列表中显示数量不一致<br /><p>[期望]</p>\n老师列表中显示数量一致', 'closed', 'zhangjch', '2018-12-07 13:17:01', 'closed', 'zhangjch', '2018-12-07 13:17:45', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (529, 528, 16, '门户系统后台-新增文章-文章内容格式问题', 'codeerror', '4', '<p>[步骤]</p>\n修改格式：<br />\n1、有序列表:有序列表下拉框中前6个都不能使用<br />\n2、无序列表:折线号、小圆圈不能使用<br />\n3、标题：标题大小之间的不明显<br />\n4、上传视频---提示服务器返回出错-不能播放<br />\n5、查找：文章不能根据查找的字符上下文切换<br />\n6、图片上传失败<br />\n预览时问题：<br />\n1、表格的线条太淡，在预览页面看不到表格的宽度<br />\n2、代码格式不明显<br />\n3、图片加载失败<br />\n4、附件，点击附件，显示找不到文件或目录<br />\n5、截图安装软件后不能使用，重启浏览器也不能使用（UEditor截图软件）<br /><br /><p>[结果]</p>\n<br /><p>[期望]</p>\n<br />', 'closed', 'dengyl', '2018-12-07 13:18:37', 'closed', 'dengyl', '2018-12-07 13:26:08', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (530, 529, 16, '班级管理-班级列表-编辑-选择班主任', 'codeerror', '2', '<p>[步骤]</p>\n选择班主任时，在教师列表中最好加一个勾选，<br /><p>[结果]</p>\n<span>如有两个老师同名时，无法确认到底选择那个老师（现在若两个老师同名会出问题）</span><span></span><br /><p>[期望]</p>\n建议加一个勾选框', 'closed', 'zhangjch', '2018-12-07 13:19:25', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (531, 530, 16, '班级管理-学生管理-查询-输入内容提示太模糊', 'codeerror', '2', '<p>[步骤]</p>\n查看查询条件<br /><p>[结果]</p>\n查询维度中：请输入内容，这个提示太模糊。<br /><p>[期望]</p>\n请输入姓名', 'closed', 'zhangjch', '2018-12-07 13:22:15', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (532, 531, 16, '班级管理-学生管理-编辑-有一个接口会报错', 'codeerror', '2', '<p>[步骤]</p>\n查看编辑的接口。<br /><p>[结果]</p>\n报错，https://uie-gq.growhappy.com.cn/api/edu/GetStudentSignList会报错（/EduQuery/Student/GetStudentSignList###timeout of 25000ms exceeded）<br /><p>[期望]</p>\n修改报错信息', 'closed', 'zhangjch', '2018-12-07 13:24:50', 'closed', 'zhangjch', '2018-12-07 13:25:12', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (533, 532, 16, '班级管理-教师管理-新增-保存后界面不会跳转到列表页面', 'interface', '3', '<p>[步骤]</p>\n点击新增按钮，输入内容，点击保存后界面不会跳转到列表页面<br /><p>[结果]</p>\n<span>保存后界面不会跳转到列表页面</span><span></span><br /><p>[期望]</p>\n保存后，界面跳转到列表页面', 'closed', 'zhangjch', '2018-12-07 13:26:53', 'closed', 'admin', '2019-02-28 15:03:18', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (534, 533, 16, '门户系统后台-系统管理-角色管理-新增', 'others', '3', '<p>[步骤]</p>\n新增角色，输入角色的名称，点击保存，再次点击保存，就会有两个一样的角色记录，建议：1.加入角色名称唯一性验证；2.点击保存，新增框关闭。<br /><p>[结果]</p>\n<br /><p>[期望]</p>\n<br />', 'closed', 'dengyl', '2018-12-07 13:27:38', 'closed', 'dengyl', '2018-12-07 13:28:00', NULL, NULL, '0', 'zt');
