INSERT INTO `bug` VALUES (652, 652, 11, '【企业信息管理-经营许可证】新增主要经营设备及设施的设计能力字段大于50还能保存', 'codeerror', '3', '<p>[步骤]登录危废监管系统，进入经营许可证页面，新增主要经营设备及设施</p>\n<p>[结果]设计能力字段大于50字可保存，其余字段如名称、规格型号大于50字不能保存，但没有提示</p>\n<p>[期望]<span>设计能力字段大于50字不能保存，其余字段如名称、规格型号大于50字不能保存，有提示</span></p>\n<p>[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome56+分辨率1366*768</span></p>', 'closed', 'zhongle', '2018-12-20 13:40:43', 'closed', 'zhongle', '2019-01-09 11:00:41', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (653, 653, 11, ' 【企业信息管理-经营许可证】新增主要环保设备及设施的设计能力字段大于50还能保存', 'codeerror', '3', '<p>[步骤]<span style=\"background-color:#FFFFFF;\">登录危废监管系统，进入经营许可证页面，新增主要环保设备及设施</span></p>\n<p>[结果]<span style=\"background-color:#FFFFFF;\">设计能力字段大于50字可保存，其余字段如名称、规格型号大于50字不能保存，但没有提示</span></p>\n<p>[期望]<span style=\"background-color:#FFFFFF;\">设计能力字段大于50字不能保存，其余字段如名称、规格型号大于50字不能保存，有提示</span></p>\n<p>[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome56+分辨率1366*768</span></p>', 'closed', 'zhongle', '2018-12-20 13:50:22', 'closed', 'zhongle', '2019-01-09 11:00:27', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (654, 654, 11, '【企业信息管理-经营许可证】新增废物经营情况，不能保存新增信息', 'codeerror', '3', '<p>\n	[步骤]<span style=\"background-color:#FFFFFF;\">登录危废监管系统，进入经营许可证页面，新增废物经营情况</span>\n</p>\n<p>\n	[结果]不能保存新增信息\n</p>\n<p>\n	[期望]<span style=\"background-color:#FFFFFF;\">正确填写新增信息后，能保存</span>\n</p>\n<p>\n	[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome56+分辨率1366*768</span>\n</p>', 'closed', 'zhongle', '2018-12-20 14:03:04', 'closed', 'zhongle', '2019-02-15 16:30:08', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (655, 655, 11, '【工业危险废物-工业年度废物报告】新增中的社会统一信用码输入文字也可以保存', 'codeerror', '2', '<p>[步骤]登录危废监管系统，进入工业年度废物报告，正确填写其他新增信息，社会统一信用码输入文字</p>\n<p>[结果]输入文字可以保存，与需求上的只能输入数字和字母不符</p>\n<p>[期望]只能输入18位的数字和字母，输入错误是有提示</p>\n<p>[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome56+分辨率1366*768</span></p>', 'closed', 'zhongle', '2018-12-20 14:18:03', 'closed', 'zhongle', '2019-01-17 11:31:38', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (656, 656, 11, '【工业危险废物-工业年度废物报告】新增中的接收单位经营许可证输入文字和超过十位数字也能保存', 'codeerror', '2', '<p>[步骤]<span style=\"background-color:#FFFFFF;\">登录危废监管系统，进入工业年度废物报告，正确填写其他新增信息，接收单位经营许可证输入文字或者超过十位数字</span></p>\n<p>[结果]<span style=\"background-color:#FFFFFF;\">输入文字或者超过十位数字都能保存，与需求上的输入十位数字不符</span></p>\n<p>[期望]只能输入十位数字的编号，输入错误有相应的提示</p>\n<p>[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome56+分辨率1366*768</span></p>', 'closed', 'zhongle', '2018-12-20 14:26:31', 'closed', 'zhongle', '2019-01-17 11:30:45', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (657, 657, 16, '在新建文章中，输入副标题，但是预览时没有副标题项', 'codeerror', '3', '<p>\n	[步骤]1.点击新建，打开新建填写内容，填写副标题，保存\n</p>\n<p>\n	2.点击预览查看副标题\n</p>\n<br /><p>\n	[结果]没有看到副标题\n</p>\n<br /><p>\n	[期望]得到反馈说改版后副标题没有再用，那么建议在新建中去掉副标题字段\n</p>\n<br />', 'closed', 'liangzy', '2018-12-20 14:50:15', 'closed', 'liangzy', '2018-12-27 11:05:29', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (658, 658, 16, '在新建文章中，“关键字”字段用处无法体现', 'codeerror', '3', '<p>\n	[步骤]点击新建，填写“关键字”字段，保存\n</p>\n<p>\n	点击预览，查看 关键字\n</p>\n<br /><p>\n	[结果]无法查看“关键字”内容\n</p>\n<br /><p>\n	[期望]如果“关键字”字段无用处，建议去除\n</p>\n<br />', 'closed', 'liangzy', '2018-12-20 15:10:15', 'closed', 'liangzy', '2018-12-27 10:59:22', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (659, 659, 16, '在新建文章中，类型选择图、视频以及链接类型时，ShowDiv字段的功能无法体现', 'codeerror', '3', '<p>\n	[步骤]1.点击新建，选择类型为图片、视频或链接\n</p>\n<p>\n	2.showDiv展开选择\"是\",保存\n</p>\n<p>\n	3.查看ShowDiv展开预览效果\n</p>\n<br /><p>\n	[结果]无法体现showdiv展开效果\n</p>\n<br /><p>\n	[期望]若showDiv选项无用，建议去除\n</p>\n<br />', 'closed', 'liangzy', '2018-12-20 15:17:38', 'closed', 'liangzy', '2018-12-27 10:53:50', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (660, 660, 16, '用账号密码登录失败会刷新出二维码登录，体验不佳', 'codeerror', '3', '<p>[步骤]1.输入不匹配的账号信息登录，点击登录</p>\n<br /><p>[结果]登录失败，刷新出二维码登录页面</p>\n<br /><p>[期望]停留在账号密码登录界面，而不是刷新出二维码</p>\n<br />', 'closed', 'liangzy', '2018-12-20 15:47:31', 'closed', 'liangzy', '2018-12-27 10:53:12', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (661, 661, 16, '批量转移信息选择栏目优化', 'codeerror', '3', '<p>[步骤]1.点击勾选文章</p>\n<p>2.点击批量转移信息</p>\n<br /><p>[结果]需要手动选择“从栏目”一项，不够方便</p>\n<br /><p>[期望]因为勾选了需要转移的文章，系统不就默认获取了是哪个模块的了吗？ 建议1.自动选择“从栏目”的信息；建议2.去掉“从栏目”字段，填写转移到什么栏目就可以了</p>\n<br />', 'closed', 'liangzy', '2018-12-20 17:12:05', 'closed', 'liangzy', '2018-12-27 10:38:51', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (662, 662, 16, '链接地址填写不正确提示不够明确', 'codeerror', '4', '<p>[步骤1.点击新建，选择类型为链接</p>\n<p>2.填写链接不正确的情况，如www.baidu.com，点击保存</p>\n<br /><p>[结果]<span>系统提示“链接地址格式不正确”，提示不明</span><span>确</span></p>\n<br /><p>[期望]可以像上面在输入框后面增加提示，地址类型为http://开头</p>\n<br />', 'closed', 'liangzy', '2018-12-20 17:27:51', 'closed', 'liangzy', '2018-12-27 09:38:56', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (663, 663, 16, '图片大小限制提醒不一致', 'codeerror', '3', '<p>[步骤]1.点击新建，点击上传超过5M的图片</p>\n<br /><p>[结果]上传失败。提示不能超过5M限制；与底下提示（<span style=\"color:#818181;font-family:Verdana;\">图片格式为:jpg、png、gif. 图片大小不超过1M</span>）不一致，混淆</p>\n<br /><p>[期望]图片大小限制一致，提示一致</p>\n<br />', 'closed', 'liangzy', '2018-12-21 09:50:44', 'closed', 'liangzy', '2018-12-27 10:22:14', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (664, 664, 12, '视频监控配置、传感器信息-普通企业用户缺少批量导入、导出权限', 'codeerror', '2', '<p>[步骤]</p>\n登录普通企业用户，按照指定模板，在视频监控配置和传感器信息模块批量导入、导出<br /><p>[结果]</p>\n视频监控配置、传感器信息-普通企业用户缺少批量导入、导出权限<br /><p>[期望]</p>\n视频监控配置、传感器信息-普通企业用户有批量导入、导出权限<br />', 'closed', 'heliujie', '2018-12-21 10:21:55', 'closed', 'heliujie', '2018-12-25 14:13:15', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (665, 665, 12, '危化品信息-列表页返回按钮逻辑有问题（多模块共性问题）', 'standard', '3', '<p>[步骤]</p>\n危化品信息列表页，先新增一条信息，然后点击返回按钮<br /><p>[结果]</p>\n返回的是新增，待填写页面<br /><p>[期望]</p>\n返回的是上一级，企业安全', 'closed', 'heliujie', '2018-12-21 10:27:33', 'closed', 'heliujie', '2018-12-25 14:07:38', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (666, 666, 12, '普通企业用户，多出删除信息的按钮', 'codeerror', '3', '<p>[步骤]</p>\n<br /><p>[结果]</p>\n<br /><p>[期望]</p>\n普通企业用户没有随便删除的权限', 'closed', 'heliujie', '2018-12-21 10:51:16', 'closed', 'heliujie', '2018-12-25 14:14:11', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (667, 667, 16, '门户 新闻资讯栏目界面优化', 'interface', '3', '<p>[步骤]点击打开门户系统，查看新闻资讯模块</p>\n<br /><p>[结果]1.新闻资讯标题没居中</p>\n<p>2.信息显示重叠不清</p>\n<br /><p>[期望]1.新闻资讯标题居中对齐</p>\n<p>2.信息显示自适应分表率；显示清楚</p>\n<br />', 'closed', 'liangzy', '2018-12-21 14:20:32', 'closed', 'liangzy', '2018-12-27 10:18:45', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (668, 668, 16, '后台编辑功能无法删除“标题图片”', 'codeerror', '3', '<p>[步骤]1.点击一个有标题图片的文章进行编辑</p>\n<p>2.点击删除标题图片；保存</p>\n<p>3.再次点击编辑，查看标题图片</p>\n<br /><p>[结果]删除功能没实现，删除、保存后标题图片仍在</p>\n<br /><p>[期望]对于不想要的标题图片可以成功删除</p>\n<br />', 'closed', 'liangzy', '2018-12-21 14:49:04', 'closed', 'liangzy', '2018-12-27 10:07:25', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (669, 669, 16, '后台文章新建和编辑默认置顶1天', 'codeerror', '3', '<p>[步骤]1.点击新建或者编辑，不勾选置顶，不勾选头条；保存</p>\n<p>2.<span>点击编辑查看是否置顶</span><span></span></p>\n<p><br /></p>\n<p>1.<span>点击新建或者编辑，不勾选置顶，勾选头条</span><span>；保存</span></p>\n<p><span><span>2.</span><span>点击编辑查看是否置顶</span><br /></span></p>\n<p><br /></p>\n<br /><p>[结果]结果1：置顶1天</p>\n<p>结果2：勾选了头条；勾选了置顶1天</p>\n<br /><p>[期望]1.若没有勾选置顶，保存后应该就不置顶；</p>\n<p>2.头条和置顶两个选项应该是单选按钮，不能同时选择，同时选择也没有意义</p>\n<br />', 'closed', 'liangzy', '2018-12-21 15:54:59', 'closed', 'liangzy', '2018-12-27 11:22:47', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (670, 670, 16, '头条和置顶功能不可用', 'codeerror', '2', '<p>[步骤]1.点击新建或编辑，勾选头条，不勾选置顶 ；或 不勾选头条，勾选置顶；保存；</p>\n<p>2.审核通过后在门户查看文章列表</p>\n<br /><p>[结果]文章设置头条或置顶不成功</p>\n<br /><p>[期望]文章设置头条或置顶后可以显示在文章列表首位</p>\n<br />', 'closed', 'liangzy', '2018-12-21 16:06:29', 'closed', 'liangzy', '2019-01-04 14:46:15', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (671, 671, 16, '链接类型的文章未能记录访问人数', 'codeerror', '3', '<p>[步骤]1.点击新建链接类型的文章，填写链接地址，保存</p>\n<p>2.审核后在门户文章列表访问；查看访问人数是否正确</p>\n<br /><p>[结果]未记录访问人数；访问人数一直为0</p>\n<br /><p>[期望]成功记录链接类型的访问人数</p>\n<br />', 'closed', 'liangzy', '2018-12-21 16:18:50', 'closed', 'liangzy', '2018-12-27 09:45:21', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (672, 672, 16, '相关链接必须有标题图片才能在门户显示', 'codeerror', '3', '<p>[步骤]1.后台点击相关链接模块，点击新建，填写链接地址，不添加标题图片；保存</p>\n<p>2.审核后在门户查看相关链接</p>\n<br /><p>[结果]查看不到新建的链接信息</p>\n<br /><p>[期望]因为链接只是一个超链接，添加标题图片也看不到，没有意义；建议不添加标题图片也能成功发布相关链接信息。</p>\n<p>如果需求设计如此，建议在新建相关链接的时候说明</p>\n<br />', 'closed', 'liangzy', '2018-12-21 16:50:25', 'closed', 'liangzy', '2018-12-27 09:38:24', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (673, 673, 16, 'webos_web端和APP端聊天，web端有延迟。且/webos/imMsg/poll接口有时候出现error中会出现error错误', 'codeerror', '1', '<p>[步骤]</p>\nweb端使用cmsadmin账号，APP端使用kqxt账号，APP端给web端发信息。<p>[结果]</p>\n当WEB端/webos/imMsg/poll<span>接口</span>中error中出现error信息后，再次进行发送信息后，web端不会更新聊天记录出现延迟问题。<br /><p>[期望]</p>\n<p>web端能够及时获取APP端的信息，不要有延迟问题。</p>\n<p>【接口错误】</p>\n<p><img src=\"{482.png}\" alt=\"\" /></p>\n<p><br /></p>', 'closed', 'zhangjch', '2018-12-21 16:54:50', 'closed', 'zhangjch', '2018-12-27 10:51:07', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (674, 674, 16, '多图上传中“在线管理”和“图片搜索”功能不可用', 'codeerror', '3', '<p>[步骤]1.点击新建或编辑，点击编辑器中的多图上传</p>\n<p>2.点击在线管理，查看图片</p>\n<p>3.点击图片搜索，输入关键字搜索图片</p>\n<br /><p>[结果]结果1.在线管理图片无法显示</p>\n<p>结果2.在线搜索功能不可用，无搜索结果</p>\n<br /><p>[期望]</p>\n<br />', 'closed', 'liangzy', '2018-12-21 17:23:07', 'closed', 'liangzy', '2018-12-27 09:06:14', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (675, 675, 11, 'Windows-发布订单失败', 'codeerror', '2', '<p>[步骤]登录chuanhhc账号在【订单管理】-【发布订单】页面发布订单，信息填写正确，点击下一步</p>\n<p>[结果]系统提示服务错误，无法下一步</p>\n<p>[期望]可以成功进入发布订单页面</p>\n<p>[环境]win10+chrome70+分辨率1920*1080</p>', 'closed', 'changguocheng', '2018-12-24 09:54:42', 'closed', 'changguocheng', '2018-12-24 15:01:33', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (676, 676, 12, '普通企业用户，增加多条信息的情况，第一条没来得及审核的，再次新增的把第一条覆盖了', 'codeerror', '1', '<p>[步骤]</p>\n登录普通企业用户，点击类似于“危化品信息”可以增加多条信息的模块，先增加、保存一条信息，再增加、保存一条或多条信息；<br /><p>[结果]</p>\n政府或运维用户待审核的列表里，只有最后一次新增信息，其他的被最后一次覆盖了；<br /><p>[期望]</p>\n<span>政府或运维用户待审核的列表里，有企业用户每一次新增的信息，不会被后边新增的覆盖；</span><br />', 'closed', 'heliujie', '2018-12-24 09:54:58', 'closed', 'heliujie', '2019-01-28 09:58:08', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (677, 677, 12, '管理员新增企业后，需要刷新后才能查看到最新增的信息； ', 'codeerror', '3', '<p>[步骤]</p>\n新增一条企业信息，查看新增的信息<br /><p>[结果]</p>\n需要刷新后才能查看到最新增的信息<br /><p>[期望]</p>\n新增后不用刷新即可查看到最新增的信息<br />', 'active', 'heliujie', '2018-12-24 09:57:03', 'jingxiaofei', NULL, NULL, NULL, NULL, '0', 'zt');
