INSERT INTO `bug` VALUES (703, 702, 16, '置顶实际时间不正确', 'codeerror', '3', '<p>\n	[步骤]1.点击新建，勾选置顶1天；设置发布日期在18小时前。保存审核。\n</p>\n<p>\n	2.查看编辑列表是否出现“置顶”；在门户查看是否置顶\n</p>\n<p>\n	<br /></p>\n<br /><p>\n	[结果]内容没有置顶\n</p>\n<br /><p>\n	[期望]设置1天置顶，应该24小时内都可以置顶，2天则是48小时可以置顶\n</p>\n<br />', 'closed', 'liangzy', '2018-12-27 15:45:45', 'closed', 'liangzy', '2019-01-07 17:00:42', NULL, NULL, '0', 'zt');
