INSERT INTO `bug` VALUES (705, 704, 16, 'IE浏览器-----门户后台上传标题图片，上传成功，点击预览，图片失败（为*）', 'codeerror', '2', '<p>\n	[步骤]\n</p>\n<p>\n	1.使用ie浏览器访问呢webos，点击后台管理\n</p>\n<p>\n	2.点击新增文章，上传标题图片\n</p>\n<p>\n	3.点击预览()\n</p>\n<p>\n	[结果]\n</p>\n<p>\n	图片展示失败(预览同意个文章图片，谷歌可以、ie不可以)\n</p>\n<p>\n	<img src=\"{506.png}\" alt=\"\" /></p>\n<p>\n	[期望]\n</p>\n<p>\n	预览时图片展示正常。\n</p>\n<p>\n	[可能原因]\n</p>\n<p>\n	获取图片的请求没有发送\n</p>', 'closed', 'zhangjch', '2018-12-27 16:52:55', 'closed', 'zhangjch', '2019-01-04 16:57:40', NULL, NULL, '0', 'zt');
