INSERT INTO `bug` VALUES (711, 826, 17, '企业信息下废水、雨水、废气模块弹框样式应保持与企业模块一致', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n环保一张图-企业信息-废水、废气、雨水<br /><p>\n	[结果]\n</p>\n弹框样式与企业模块不一致，缺少排放口汇总信息，排放口选择下拉框<br /><p>\n	[期望]\n</p>\n与企业模块弹框样式一致，增加排放口汇总信息，增加排放口选择下拉框，排放口类型默认为当前选择类型', 'closed', 'qiankairu', '2019-01-05 10:28:37', 'closed', 'wangchh', '2019-02-20 08:44:52', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (712, 710, 11, 'Windows-邮箱注册时偶发性收不到激活', 'codeerror', '2', '<p>\n	[步骤]1、在系统注册页面输入正确的邮箱和密码，点击注册\n</p>\n<p>\n	2、系统弹出确认查看激活码提示，点击确定\n</p>\n<p>\n	[结果]邮箱收不到激活邮件\n</p>\n<p>\n	[期望]邮箱可以收到激活邮件\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2018-12-28 10:50:44', 'closed', 'changguocheng', '2019-01-08 14:13:01', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (713, 711, 11, 'Windows-环保科普内容显示乱码', 'codeerror', '1', '<p>\n	[步骤]登录系统点击环保科普详情，进入环保科普页面\n</p>\n<p>\n	[结果]页面内容显示乱码\n</p>\n<p>\n	[期望]页面内容不会乱码\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2018-12-28 10:55:43', 'closed', 'changguocheng', '2019-01-16 10:02:41', NULL, NULL, '0', 'zt');
