INSERT INTO `bug` VALUES (736, 734, 11, 'Windows-无法添加题库', 'codeerror', '2', '<p>\n	[步骤]<span style=\"background-color:#FFFFFF;\">使用msopen登录后台系统，在【题库管理】页面点击+，填写正确信息，点击保存</span> \n</p>\n<p>\n	[结果]系统弹出黑框，无法成功添加\n</p>\n<p>\n	[期望]系统提示添加成功，可以成功添加题库\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2018-12-28 17:29:26', 'closed', 'changguocheng', '2019-02-27 10:13:45', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (737, 735, 11, 'Windows-无法修改题库', 'codeerror', '2', '<p>\n	[步骤]<span style=\"background-color:#FFFFFF;\">使用msopen登录后台系统，在【题库管理】页面选择党的十九大修改</span> \n</p>\n<p>\n	[结果]无法点击\n</p>\n<p>\n	[期望]可以点击并且填写正确的信息可以修改\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2018-12-28 17:33:37', 'closed', 'changguocheng', '2019-01-16 10:17:50', NULL, NULL, '0', 'zt');
