INSERT INTO `bug` VALUES (739, 737, 17, '基本信息页面只显示3个监测因子，缺了臭气监测因子​', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n<p>\n	1.用户进入环保一张图-空气质量-臭气站；\n</p>\n<p>\n	2.基本信息页面只显示3个监测因子，缺了臭气监测因子\n</p>\n<br /><p>\n	[结果]\n</p>\n<span>基本信息页面只显示3个监测因子，缺了臭气监测因子</span><br /><p>\n	[期望]\n</p>\n<p>\n	显示全部监测因子\n</p>\n<br />', 'closed', 'wangsw', '2018-12-28 17:40:27', 'closed', 'wangsw', '2018-12-29 13:44:32', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (740, 738, 11, 'Windows-无法下载题库', 'codeerror', '2', '<p>\n	[步骤]<span style=\"background-color:#FFFFFF;\">使用msopen登录后台系统，在【题库管理】页面，点击下载判断题模板</span> \n</p>\n<p>\n	[结果]页面跳转显示文件不存在，无法下载题库模板\n</p>\n<p>\n	[期望]可以下载题库模板\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2018-12-28 17:42:39', 'closed', 'changguocheng', '2019-02-15 14:54:05', NULL, NULL, '0', 'zt');
