INSERT INTO `bug` VALUES (742, 740, 11, 'Windows-无法预览试卷', 'codeerror', '2', '<p>\n	[步骤]<span style=\"background-color:#FFFFFF;\">使用msopen登录后台系统，在【试卷管理】页面选择1000.4预览</span> \n</p>\n<p>\n	[结果]系统无响应\n</p>\n<p>\n	[期望]系统有响应并且可以成功预览\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2018-12-28 17:52:57', 'closed', 'changguocheng', '2019-02-15 14:58:22', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (743, 741, 11, 'Windows-无法重置搜索项和列表项信息', 'codeerror', '3', '<p>\n	[步骤]<span style=\"background-color:#FFFFFF;\">使用msopen登录后台系统，在【试卷管理】页面搜索框输入t，点击搜索，列表显示符合条件的项，点击重置</span> \n</p>\n<p>\n	[结果]系统没有响应，点击无效，无法重置搜索项\n</p>\n<p>\n	[期望]系统有响应，点击有效，可以重置搜索项\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2018-12-28 17:57:43', 'closed', 'changguocheng', '2019-03-13 10:32:37', NULL, NULL, '0', 'zt');
