INSERT INTO `bug` VALUES (803, 800, 11, '【企业信息管理-车辆管理信息】车辆信息列表不能查看车辆详情', 'codeerror', '2', '<p>\n	[步骤]<span style=\"background-color:#FFFFFF;\">登录危废监控系统，进入车辆管理信息</span>\n</p>\n<p>\n	[结果]车辆管理信息列表<span style=\"background-color:#FFFFFF;\">没有查看车辆管理详情的按钮</span>\n</p>\n<p>\n	[期望]车辆<span style=\"background-color:#FFFFFF;\">列表有查看详情按钮，可以查看车辆基本信息</span>\n</p>\n<p>\n	[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome61+1366*768</span>\n</p>', 'closed', 'zhongle', '2019-01-03 09:29:00', 'closed', 'zhongle', '2019-01-29 13:21:29', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (804, 801, 13, 'Android-消息免打扰无效', 'codeerror', '2', '<p>\n	[步骤]1、使用一个测试账号在小米手机登录软件，在【个人中心】-【设置】页面点击消息通知，点击开启消息免打扰，时间段为00：00到23：00\n</p>\n<p>\n	2、使用管理员账号在华为手机登录软件，发送11111\n</p>\n<p>\n	[结果]测试账号登录的软件又消息提醒\n</p>\n<p>\n	[期望]在消息免打扰时间段内的时间接收消息应该没有提示\n</p>\n<p>\n	[环境]MI5s Android6.0.1\n</p>', 'resolved', 'changguocheng', '2019-01-03 09:30:48', 'changguocheng', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (805, 802, 1, 'Android-举报跟踪-输入事件编号查询APP会闪退', 'codeerror', '1', '<p>\n	</p><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[步骤]\n	</p>\n<span style=\"background-color:#FFFFFF;\">登录APP，进入大气网格化-举报跟踪，输入事件编号后点【查询】</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[结果]\n	</p>\n<span style=\"background-color:#FFFFFF;\">APP闪退</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[期望]\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		能正常查询事件\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		[环境]\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		魅蓝Note6&nbsp; &nbsp; 7.1.2\n	</p>\n\n<p>\n	<br /></p>\n<br />', 'closed', 'wuhaili', '2019-01-03 09:32:49', 'closed', 'wuhaili', '2019-01-09 11:26:59', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (806, 803, 11, '【企业信息管理-经营许可证】经营许可证页面没有删除功能', 'codeerror', '2', '<p>\n	[步骤]登录危废监控系统，进入经营许可证页面\n</p>\n<p>\n	[结果]经营许可证页面没有删除按钮\n</p>\n<p>\n	[期望]<span>经营许可证页面有删除按钮，可进行删除操作</span>\n</p>\n<p>\n	[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome61+分辨率1366*768</span>\n</p>', 'closed', 'zhongle', '2019-01-03 09:39:34', 'closed', 'zhongle', '2019-02-19 10:40:11', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (807, 804, 1, 'iOS-事件跟踪-输入正确的事件编号查询APP会闪退', 'codeerror', '1', '<p>\n	<br /></p>\n<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[步骤]\n</p>\n<span style=\"background-color:#FFFFFF;\">登录APP，进入大气网格化-举报跟踪，输入正确的事件编号后点【查询】</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[结果]\n</p>\n<span style=\"background-color:#FFFFFF;\">APP闪退，</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[期望]\n</p>\n<p style=\"background-color:#FFFFFF;\">\n	能正常查询事件\n</p>\n<p style=\"background-color:#FFFFFF;\">\n	[环境]\n</p>\n<p style=\"background-color:#FFFFFF;\">\n	iPhone6 plus&nbsp; 11.4.1\n</p>\n<br />', 'closed', 'wuhaili', '2019-01-03 09:44:13', 'closed', 'wuhaili', '2019-01-09 13:48:59', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (808, 805, 11, '【企业信息管理-经营许可证】建议经营许可证详情页面增加返回上一级的按钮', 'codeerror', '3', '<p>\n	[步骤]登录危废监控系统，进入经营许可证页面，点击详情\n</p>\n<p>\n	[结果]详情页面没有返回上一级的按钮，不能从详情页面返回到经营许可证列表页\n</p>\n<p>\n	[期望]建议在经营许可证详情页面增加返回上一级的按钮\n</p>\n<p>\n	[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome61+分辨率1366*768</span>\n</p>', 'closed', 'zhongle', '2019-01-03 10:01:20', 'closed', 'zhongle', '2019-02-27 10:17:29', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (809, 806, 13, 'Android-一个账号可以多台设备同时登录', 'codeerror', '3', '<p>\n	[步骤]1、使用管理员账号在小米手机登录软件\n</p>\n<p>\n	2、再使用一样的账号在华为手机登录软件\n</p>\n<p>\n	[结果]可以同时登录无冲突\n</p>\n<p>\n	[期望]一个账号应该只能同时登录一个设备\n</p>\n<p>\n	[环境]MI5s Android6.0.1\n</p>', 'resolved', 'changguocheng', '2019-01-03 10:02:18', 'pingaoy', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (810, 807, 1, ' iOS-事件上报-现场取证添加的录音无法播放', 'codeerror', '3', '<p>\n	</p><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[步骤]\n	</p>\n<span style=\"background-color:#FFFFFF;\">登录APP，进入大气网格化-事件上报，现场取证添加录音后，试播放添加好的录音</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[结果]\n	</p>\n<span style=\"background-color:#FFFFFF;\">无法播放录音，录音播放界面依然能进行语音录制</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[期望]\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		添加的录音能正常播放\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		[环境]\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		iPhone6 plus&nbsp; 11.4.1\n	</p>\n	<div>\n		<br /></div>\n\n<br />', 'closed', 'wuhaili', '2019-01-03 10:02:41', 'closed', 'wuhaili', '2019-01-09 13:39:23', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (811, 808, 1, ' iOS-事件上报-现场取证添加拍照的图片和录制的视频界面【取消】按钮点击无效', 'codeerror', '3', '<p>\n	</p><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[步骤]\n	</p>\n<span style=\"background-color:#FFFFFF;\">登录APP，进入大气网格化-事件上报-现场取证添加现场拍照图片或现场录制视频的界面，点击【取消】拍摄或录制按钮</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[结果]\n	</p>\n<span style=\"background-color:#FFFFFF;\">【取消】按钮点击无效</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[期望]\n	</p>\n	<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		拍照的图片和录制的视频界面【取消】按钮能正常使用\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		[环境]\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		iPhone6 plus&nbsp; 11.4.1\n	</p>\n\n<br />', 'closed', 'wuhaili', '2019-01-03 10:14:10', 'closed', 'wuhaili', '2019-01-09 13:40:42', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (812, 809, 1, 'Android-公众举报-现场取证添加的图片点击查看大图APP会闪退', 'codeerror', '2', '<p>\n	<br /></p>\n<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[步骤]\n</p>\n<span style=\"background-color:#FFFFFF;\">登录APP，进入大气网格化-事件上报-现场取证添加拍照或相册的图片完成后，点击查看大图</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[结果]\n</p>\n<span style=\"background-color:#FFFFFF;\">APP会闪退</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[期望]\n</p>\n<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	可以正常查看添加好的图片大图\n</p>\n<p style=\"background-color:#FFFFFF;\">\n	[环境]\n</p>\n<p>\n	<br /></p>\n<span style=\"background-color:#FFFFFF;\">魅蓝Note6&nbsp; &nbsp; 7.1.2</span><br />', 'closed', 'wuhaili', '2019-01-03 10:32:25', 'closed', 'wuhaili', '2019-01-09 11:26:47', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (813, 810, 1, 'iOS-事件上报-联系电话填写不正确时应该不能上报信息', 'codeerror', '3', '<p>\n	<br /></p>\n<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[步骤]\n</p>\n<span style=\"background-color:#FFFFFF;\">登录APP，进入大气网格化-事件上报-先填写必要的信息再填写错误格式的电话号码，点上报</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[结果]\n</p>\n<span style=\"background-color:#FFFFFF;\">第一次上报有提示“电话号码不正确”，不修改再点击第二次就可以上报信息</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[期望]\n</p>\n<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	联系电话填写不正确时应该不能上报信息，限制联系电话的填写格式为数字\n</p>\n<p style=\"background-color:#FFFFFF;\">\n	[环境]\n</p>\n<p style=\"background-color:#FFFFFF;\">\n	iPhone6 plus&nbsp; 11.4.1\n</p>\n<div>\n	<br /></div>\n<p>\n	<br /></p>\n<br />', 'closed', 'wuhaili', '2019-01-03 10:53:05', 'closed', 'wuhaili', '2019-01-09 13:47:31', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (814, 811, 1, ' iOS-事件上报-在疑似原因弹出框选择原因类型后应该要点【确定】才能保存', 'codeerror', '3', '<p>\n	</p><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[步骤]\n	</p>\n<span style=\"background-color:#FFFFFF;\">登录APP，进入大气网格化-事件上报-先填写必要的信息再填写疑似原因，在<span style=\"background-color:#FFFFFF;\">疑似原因弹出框中选择原因类型后点弹框周围的空白处</span></span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[结果]\n	</p>\n<span style=\"background-color:#FFFFFF;\"><span style=\"background-color:#FFFFFF;\">点弹框周围的空白处也可以保存信息</span></span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		[期望]\n	</p>\n	<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n		应该要点【确定】才能保存信息，建议【确定】按钮可以跟安卓的一样\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		<br /></p>\n	<p style=\"background-color:#FFFFFF;\">\n		iPhone6 plus&nbsp; 11.4.1\n	</p>\n\n<br />', 'closed', 'wuhaili', '2019-01-03 11:03:09', 'closed', 'wuhaili', '2019-01-09 13:41:39', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (815, 812, 11, '【工业危险废物-危险废物信息管理】危险废物信息管理页面不能查看详情', 'codeerror', '2', '<p>\n	[步骤]登陆危废监控系统，进入危险废物信息管理\n</p>\n<p>\n	[结果]危险废物信息管理列表页面没有查看详情\n</p>\n<p>\n	[期望]危险废物信息列表可以查看信息的详情\n</p>\n<p>\n	[环境]<span style=\"background-color:#FFFFFF;\">win7+Chrome61+分辨率1366*768</span>\n</p>', 'closed', 'zhongle', '2019-01-03 11:09:00', 'closed', 'zhongle', '2019-02-15 15:24:25', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (816, 813, 13, 'Android-扬声器和听筒效果不对', 'codeerror', '2', '<p>\n	[步骤]1、使用测试账号小米5s手机登录软件\n</p>\n<p>\n	2、使用考勤用户在小米8手机登录软件\n</p>\n<p>\n	3、选择考勤用户给测试用户拨打语音会议，可以正常接通\n</p>\n<p>\n	[结果]在手机音量低于50%时，听筒的声音比扬声器声音大，在音量大于50%时，扬声器音量比听筒大\n</p>\n<p>\n	[期望]在音量较小时听筒声音也应该比扬声器小\n</p>\n<p>\n	[环境]MI5s Android6.0.1\n</p>', 'resolved', 'changguocheng', '2019-01-03 11:38:31', 'changguocheng', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (817, 814, 1, ' iOS-行政入口-环境页面有些文字显示重叠', 'interface', '4', '<p>\n	[步骤]\n</p>\n登录APP，进入大气网格化-行政入口-环境页面查看信息<br /><p>\n	[结果]\n</p>\n环境页面有些文字显示重叠<br /><p>\n	[期望]\n</p>\n<p>\n	正常显示信息\n</p>\n<p>\n	</p><p style=\"background-color:#FFFFFF;\">\n		[环境]\n	</p>\n	<p style=\"background-color:#FFFFFF;\">\n		iPhone6 plus&nbsp; 11.4.1\n	</p>\n', 'closed', 'wuhaili', '2019-01-03 11:46:06', 'closed', 'wuhaili', '2019-01-09 13:51:30', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (818, 816, 1, ' iOS-行政入口-云图-站点详情页面的实时数据的时间应该实时显示当前的时间', 'codeerror', '3', '<p>\n	<br /></p>\n<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[步骤]\n</p>\n<span style=\"background-color:#FFFFFF;\">登录APP，进入大气网格化-行政入口-云图-站点详情页面查看数据</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[结果]\n</p>\n<span style=\"background-color:#FFFFFF;\">站点详情页面的实时数据的时间显示不正确</span><br /><p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	[期望]\n</p>\n<p class=\"stepTitle\" style=\"background-color:#FFFFFF;\">\n	站点详情页面的实时数据的时间应该实时显示当前的时间\n</p>\n<p style=\"background-color:#FFFFFF;\">\n	[环境]\n</p>\n<p style=\"background-color:#FFFFFF;\">\n	iPhone6 plus&nbsp; 11.4.1\n</p>\n<br />', 'closed', 'wuhaili', '2019-01-03 18:02:43', 'closed', 'wuhaili', '2019-01-22 12:16:23', NULL, NULL, '0', 'zt');
