INSERT INTO `bug` VALUES (879, 878, 11, 'Windows-没有enter登录功能', 'others', '3', '<p>\n	[步骤]在登录页面输入正确的账号、密码，点击erter\n</p>\n<p>\n	[结果]无法执行登录操作\n</p>\n<p>\n	[期望]可以通过点击enter键来执行登录操作\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-01-09 08:43:53', 'closed', 'changguocheng', '2019-01-11 11:35:33', NULL, NULL, '0', 'zt');
