INSERT INTO `bug` VALUES (889, 888, 11, 'Windows-课件视频无法预览', 'codeerror', '2', '<p>\n	[步骤]在系统管理中心页面选择上传课件，在上传附件成功之后，点击预览\n</p>\n<p>\n	[结果]页面进入下载页面，无法预览\n</p>\n<p>\n	[期望]可以成功预览附件\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-01-09 10:20:48', 'closed', 'changguocheng', '2019-01-25 17:16:23', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (890, 889, 11, 'Windows-未选择是否有账号也可以进入添加信息弹框页面', 'codeerror', '4', '<p>\n	[步骤]1、在系统管理中心添加学员，在学员管理页面点击添加学员，页面弹出是否有账号弹框\n</p>\n<p>\n	2、点击确定\n</p>\n<p>\n	[结果]弹框进入填写信息页面\n</p>\n<p>\n	[期望]未选择是否有账号时可以提醒请做出选择并且无法进入填写信息页面\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-01-09 10:42:41', 'closed', 'changguocheng', '2019-03-14 09:50:54', NULL, NULL, '0', 'zt');
