INSERT INTO `bug` VALUES (946, 945, 11, 'Windows-后台添加文章未在前台显示', 'codeerror', '2', '<p>\n	[步骤]1、使用系统管理员账号登录后台系统，在内容管理-文章管理发布文章\n</p>\n<p>\n	2、在前端环保法律法规查看添加信息\n</p>\n<p>\n	[结果]前端没有添加信息显示，数据库中又添加信息\n</p>\n<p>\n	[期望]前端显示添加信息\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-01-09 16:35:37', 'closed', 'changguocheng', '2019-01-11 13:37:32', NULL, NULL, '0', 'zt');
