INSERT INTO `bug` VALUES (971, 971, 16, 'webos页面-从后台管理审核成功的文章推送到webos聊天框中，但是时间不对应', 'interface', '3', '<p>\n	[步骤]\n</p>\n<p>\n	1.在后台管理，新闻资讯栏目中，新增文章，审核通过\n</p>\n<p>\n	2.webos中的新闻资讯中查看发布的文章\n</p>\n<p>\n	[结果]\n</p>\n发现webos的时间与发布文章的时间不对应<br /><p>\n	[期望]\n</p>\n时间能够统一<br /><p>\n	[截图]\n</p>\n<p>\n	<img src=\"{720.png}\" alt=\"\" /></p>', 'resolved', 'zhangjch', '2019-01-11 09:20:40', 'zhangjch', NULL, NULL, NULL, NULL, '0', 'zt');
