INSERT INTO `bug` VALUES (1473, 1473, 16, 'web端--教务管理--基础信息--学期设置--结束时间逻辑有问题。', 'codeerror', '2', '<p>\n	学期设置中开始时间和结束时间，逻辑上应该是上学的时间，<span style=\"background-color:#E53333;\">但是在结束时间时，班牌中今日课程显示有问题。</span>\n</p>\n<p>\n	[期望]\n</p>\n<p>\n	<span>开始时间和结束时间，包括结束时间都正常显示今日课程的信息。</span>\n</p>\n<p>\n	【截图】\n</p>\n<p>\n	<img src=\"{1137.png}\" alt=\"\" /></p>', 'closed', 'zhangjch', '2019-01-25 13:00:27', 'closed', 'zhangjch', '2019-04-29 12:12:27', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1474, 1474, 16, '班牌端--考勤的数据显示有问题---应该是查询的逻辑有问题。', 'codeerror', '2', '<p>\n	【问题】\n</p>\n<p>\n	一年级(1)班，今天我值签到了一个学生，但是班牌上显示实到3人。\n</p>\n<p>\n	【造成原因】\n</p>\n<p>\n	在sql中查询实到学生时，添加一个时间。\n</p>\n<p>\n	【截图】\n</p>\n<p>\n	<img src=\"{1138.png}\" alt=\"\" /></p>\n<p>\n	<img src=\"{1139.png}\" alt=\"\" /></p>\n<p>\n	<img src=\"{1140.png}\" alt=\"\" /></p>\n<br />', 'closed', 'zhangjch', '2019-01-25 13:00:50', 'closed', 'zhangjch', '2019-03-15 10:19:01', NULL, NULL, '0', 'zt');
