INSERT INTO `bug` VALUES (1515, 1515, 12, '相关项目字段的下拉列表勾选后需要点击两次两次保存才能成功', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n在企业信息列表中勾选一家企业，点击修改按钮，在企业详细信息里边修改相关项目，然后点击保存；<br /><p>\n	[结果]\n</p>\n保存时需要点击两次“保存按钮”；<br /><p>\n	[期望]\n</p>\n<p>\n	只需点击一次保存按钮，既可保存成功；\n</p>', 'active', 'heliujie', '2019-01-28 15:40:39', 'jingxiaofei', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1516, 1516, 11, 'Windows-通行订单确认回单功能错误', 'codeerror', '2', '<p>\n	[步骤]1、登录金逸账号发布通行订单，选择委托方为传化\n</p>\n<p>\n	2、登录传化账号确认订单\n</p>\n<p>\n	3、走到确认回单流程时，在传化账号下我的运单模块有确认回单按钮，但是确认回单页面无法确认回单\n</p>\n<p>\n	4、在金逸账号下我的运单模块没有确认回单按钮，但是在货物跟踪模块可以确认回单\n</p>\n<p>\n	[结果]没有统一确认回单用户\n</p>\n<p>\n	[期望]确定确认回单的对象，做统一处理\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-01-28 15:44:00', 'closed', 'changguocheng', '2019-03-26 16:04:31', NULL, NULL, '0', 'zt');
