INSERT INTO `bug` VALUES (1518, 1518, 11, 'Windows-审批成功的车辆无法进行派车', 'codeerror', '2', '<p>\n	[步骤]1、使用省外账号新增车辆\n</p>\n<p>\n	2、使用whpadmin账号登录系统，审批通过新增车辆\n</p>\n<p>\n	3、使用省外账号派车\n</p>\n<p>\n	[结果]<span>选择车牌号控件没有新增的审批通过的车辆进行选择</span>\n</p>\n<p>\n	[期望]<span>选择车牌号控件有新增的审批通过的车辆进行选择</span>\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-01-28 15:54:36', 'closed', 'changguocheng', '2019-02-28 14:27:31', NULL, NULL, '0', 'zt');
