INSERT INTO `bug` VALUES (1670, 1667, 13, '主页点击刷新很久才加载页面，其中有一个接口报500错误', 'codeerror', '2', '<p>\n	[步骤]1.app主页-点击右上角+号-点击刷新页面\n</p>\n<br /><p>\n	[结果]1.页面空白，刷新很久\n</p>\n<p>\n	2.点击“发起聊天”也有类似情况发生\n</p>\n<p>\n	[期望]\n</p>\n<p>\n	[报错接口]\n</p>\n<p>\n	http://36.22.178.70:9006/Models/HomePageImg.svc/GetHPImgInfo?&nbsp; &nbsp;500错误\n</p>\n<p>\n	[报错截图]\n</p>\n<p>\n	<img src=\"{1380.png}\" alt=\"\" /></p>', 'closed', 'liangzy', '2019-02-20 16:15:31', 'closed', 'liangzy', '2019-02-26 10:05:04', NULL, NULL, '0', 'zt');
