INSERT INTO `bug` VALUES (1789, 1787, 13, '服务号-新的邮件-登录、界面操作问题', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n<br /><p>\n	[结果]1.登录新邮箱，测试了QQ邮箱、163邮箱、139邮箱。只有139邮箱可以成功登录，其他均提示密码错误（密码正确的）\n</p>\n<p>\n	2.点击“新的邮件”进入收件箱界面，点击“查看邮件、写邮件、附件、设置、刷新”都无响应，切换到“已发送、草稿箱”等其他页面再切换回来，才可以点击。\n</p>\n<p>\n	<img src=\"{1528.png}\" alt=\"\" /></p>\n<br /><p>\n	[期望]\n</p>\n<br />', 'closed', 'liangzy', '2019-03-06 14:03:11', 'closed', 'zhangjch', '2019-04-17 08:33:49', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1790, 1788, 13, '查看他人动态显示数量和实际看得到的数量不一致', 'codeerror', '4', '<p>\n	[步骤]查看他人动态，动态数量实际是10显示也是10。由于其中包括了2条私密动态，实际看得到的动态只有8条，但是数量显示10。个人建议性是显示的数量为当前双方关系（好友、非好友等）实际看得到的数量。\n</p>\n<p>\n	<br /></p>\n<img src=\"{1529.png}\" alt=\"\" /><br /><p>\n	[结果]\n</p>\n<br /><p>\n	[期望]\n</p>\n<br />', 'closed', 'liangzy', '2019-03-06 14:48:58', 'closed', 'zhangjch', '2019-04-19 10:48:09', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1791, 1789, 13, '学校圈按条件查询动态-动态类型没有选项', 'codeerror', '4', '<p>\n	[步骤学校圈-条件查询-动态类型-没有其他类型选择\n</p>\n<br /><p>\n	[结果]\n</p>\n<br /><p>\n	[期望]\n</p>\n<br />', 'resolved', 'liangzy', '2019-03-06 15:16:14', 'liangzy', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1792, 1790, 13, 'IOS-门户后台发布一个文章，ios消息栏中显示信息数，点击主页，点击新闻播报，就会无效。', 'codeerror', '2', '<p>\n	[步骤]\n</p>\n<p>\n	1.使用IOS8登录admin账号\n</p>\n<p>\n	2.IOS进入到消息列，门户后台审核已经审核通过文章。\n</p>\n<p>\n	3.IOS点击主页，主页的的按钮大部分都失效，尤其是新闻播报、发起签字等功能\n</p>\n<p>\n	4.当系统反映好后，新闻播报进入好几层页面。\n</p>\n<p>\n	【期望】\n</p>\n<p>\n	解决这种卡顿时间过长的问题。\n</p>', 'closed', 'zhangjch', '2019-03-06 16:38:01', 'closed', 'zhangjch', '2019-03-07 09:31:10', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1793, 1791, 13, 'IOS、安卓-GPS定位有问题', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n<p>\n	1.账号admin（ios8）和账号kqxt（安卓）共享位置\n</p>\n<p>\n	2。查看两个手机的显示位置\n</p>\n<p>\n	[结果]\n</p>\n<p>\n	位置有点相差过大\n</p>\n<p>\n	<img src=\"{1531.png}\" alt=\"\" /></p>\n<p>\n	[期望]\n</p>\n位置相对较准确', 'closed', 'zhangjch', '2019-03-06 16:43:53', 'closed', 'zhangjch', '2019-04-19 10:47:46', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1794, 1792, 13, '修改手机号的验证短信内容提示不正确', 'codeerror', '4', '<p>\n	[步骤]修改手机号-验证新手机号码，接收短信信息\n</p>\n<br /><p>\n	[结果]短信提示为“账号注册”\n</p>\n<br /><p>\n	[期望]建议<span>类似的</span>为“您正在修改xxx手机号”，若非本人操作，请忽略。。。\n</p>\n<div>\n	<span><br /></span> \n</div>', 'resolved', 'liangzy', '2019-03-06 16:49:14', 'liangzy', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1795, 1793, 13, 'IOS---相对于安卓APP来说，在聊天页面，点击+号，下面的功能少了一个文件发送的功能', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n<p>\n	1.登录智慧校园IOS版本\n</p>\n<p>\n	2.点击信息\n</p>\n<p>\n	3.选择一个记录\n</p>\n<p>\n	4.点击+号\n</p>\n<p>\n	[结果]\n</p>\n<p>\n	IOS-APP相对安卓-APP来说，少了个文件的功能\n</p>\n<p>\n	<img src=\"{1532.png}\" alt=\"\" /></p>\n<p>\n	[期望]\n</p>\n<p>\n	加上文件的功能\n</p>', 'closed', 'zhangjch', '2019-03-06 16:49:37', 'closed', 'liangzy', '2019-03-07 10:19:28', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1796, 1794, 11, '应急事件管理的未完成事件的事发时间的查询条件无法查询', 'codeerror', '2', '<p>\n	[步进入应急事件管理的未完成事件，选择事发时间的日期，点击查询\n</p>\n<p>\n	[结果]无法根据查询的条件得到查询结果\n</p>\n<p>\n	[期望]能够根据事发时间查询到相关的事件\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-03-07 17:38:17', 'closed', 'zhongle', '2019-03-15 10:56:40', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1797, 1795, 11, '应急事件管理的事件的处理操作功能没有实现', 'codeerror', '2', '<p>\n	[步骤]进入应急事件管理，点击事件的处理功能\n</p>\n<p>\n	[结果]点击处理功能后没有任何反应，处理功能没有实现\n</p>\n<p>\n	[期望]点击处理功能后能有相应的处理方法\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-03-07 17:49:50', 'closed', 'zhongle', '2019-03-29 14:19:58', NULL, NULL, '0', 'zt');
