INSERT INTO `bug` VALUES (1799, 1797, 11, 'Windows-系统无法挂断电话', 'codeerror', '3', '<p>\n	[步骤]1、登录一张图系统\n</p>\n<p>\n	2、点击进入应急指挥页面\n</p>\n<p>\n	3、点击拨打浙江乍浦美福码头仓储公司，系统跳出提起话机提示\n</p>\n<p>\n	4、点击提示弹框挂断\n</p>\n<p>\n	[结果]无法挂断并且弹框跳动\n</p>\n<p>\n	[期望]可以挂断并且弹框不会乱跳动\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-03-07 18:12:38', 'closed', 'changguocheng', '2019-03-27 14:58:59', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1800, 1798, 11, '组织机构的姓名查询功能不支持模糊查询', 'codeerror', '4', '<p>\n	[步骤]进入组织机构的页面，在姓名查询搜索框中姓名中的一个字，点击查询\n</p>\n<p>\n	[结果]不能查到相关的信息，不支持模糊查询\n</p>\n<p>\n	[期望]可以支持模糊查询\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-03-07 18:18:11', 'closed', 'zhongle', '2019-03-15 11:15:21', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1801, 1799, 11, '快速点击组织机构的新增页面的保存按钮，可以保存多条相同的信息', 'codeerror', '3', '<p>\n	[步骤]打开组织机构的新增页面，正确填写相关信息，连续点击保存按钮\n</p>\n<p>\n	[结果]能保存多条相同的信息\n</p>\n<p>\n	[期望]只能保存一条信息，有相同信息的时候有相应的提示\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-03-07 18:31:43', 'closed', 'zhongle', '2019-03-15 11:17:28', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (1802, 1800, 11, 'Windows-刷新页面后通话队列信息清空', 'codeerror', '3', '<p>\n	[步骤]1、登录危化品监管云系统\n</p>\n<p>\n	2、点击进入应急指挥模块\n</p>\n<p>\n	3、有通话信息之后刷新页面\n</p>\n<p>\n	[结果]通话队列信息清空\n</p>\n<p>\n	[期望]通话队列信息不会清空\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-03-07 18:43:11', 'closed', 'changguocheng', '2019-03-27 15:06:19', NULL, NULL, '0', 'zt');
