INSERT INTO `bug` VALUES (2082, 2080, 11, 'Windows-查看更多通话记录跳转错误', 'codeerror', '3', '<p>\n	[步骤]1、登录协同监管云系统\n</p>\n<p>\n	2、在应急指挥-通话记录点击更多\n</p>\n<p>\n	[结果]页面跳转到<a href=\"http://localhost:8081/#/address-manage/call-log\">http://localhost:8081/#/address-manage/call-log</a> \n</p>\n<p>\n	[期望]正确显示更多通话记录\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-16 14:01:20', 'closed', 'changguocheng', '2019-04-19 16:43:46', NULL, NULL, '0', 'zt');
