INSERT INTO `bug` VALUES (2101, 2100, 11, 'Windows-在线考查询显示不合理', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在个人中心-在线考试查询缺考和进行中考试信息\n</p>\n<p>\n	[结果]缺口显示的是进行中和已删除的项，进行中暂无数据\n</p>\n<p>\n	[期望]正确显示符合搜索条件的项\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-17 14:39:56', 'closed', 'changguocheng', '2019-04-25 10:55:11', NULL, NULL, '0', 'zt');
