INSERT INTO `bug` VALUES (2130, 2145, 11, 'Windows-同步一企一档无法添加政府部门信息', 'codeerror', '3', '<p>\n	[步骤]1、登录系统后台\n</p>\n<p>\n	2、进入单位管理-单位管理，点击同步一企一档\n</p>\n<p>\n	[结果]无法添加政府部门\n</p>\n<p>\n	[期望]可以添加政府部门\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-19 14:29:03', 'closed', 'changguocheng', '2019-04-26 14:46:33', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2131, 2129, 11, '存量管理的右下角页码显示的页码数和实际显示的不一致', 'codeerror', '4', '<p>\n	[步骤]进入存量管理，转到第11页的列表显示，对列表信息进行按危化品名称搜索\n</p>\n<p>\n	[结果]页面显示的是<span>搜索结果的第一页，但是页码还是显示的是第11页</span>\n</p>\n<p>\n	[期望]<span>页面显示的是</span><span>搜索结果的第一页，且页码显示第1页</span>\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-04-18 13:58:39', 'closed', 'zhongle', '2019-04-23 10:57:06', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2132, 2130, 11, '流入管理的新增页面的UN编号输入框输入超过4位数字后会有错误提示，但是没有相应的提示内容', 'codeerror', '3', '<p>\n	[步骤]进入流入管理的新增页面，在UN编号输入框输入超过4位数字\n</p>\n<p>\n	[结果]系统会有错误的提示，但是没有提示的内容\n</p>\n<p>\n	[期望]系统出现错误的提示，且有相应的提示内容\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-04-18 14:50:31', 'closed', 'zhongle', '2019-05-06 14:03:33', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2133, 2131, 11, 'Windows-全国排名大图显示不合理', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在统计分析点击查看全国排名大图\n</p>\n<p>\n	[结果]全国排名大图显示和未放大图展示数据不一致而且大图的×显示不完整\n</p>\n<p>\n	[期望]大图显示和未放大显示数据一致并且×完整显示\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-18 15:19:03', 'closed', 'zhangbin', '2019-04-30 11:18:52', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2134, 2132, 11, ' 存量管理模块的按危化品种类搜索条件搜索后，点击重置按钮显示暂无数据', 'codeerror', '4', '<p>\n	[步骤]存量管理模块按危化品种类搜索，搜索完成后点击重置按钮\n</p>\n<p>\n	[结果]列表信息显示暂无数据显示，再次点击重置按钮后才能显示全部的信息\n</p>\n<p>\n	[期望]点击重置按钮后输入框数据清除并且列表显示全部的信息\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-04-18 15:47:49', 'closed', 'zhongle', '2019-05-10 10:50:39', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2135, 2133, 11, '流量存量模块的修改页面的必填项危化品类别输入框没有信息', 'codeerror', '3', '<p>\n	[步骤]进入流量存量模块，点击修改按钮，进入修改页面\n</p>\n<p>\n	[结果]修改页面的必填项危化品类别输入框没有信息，并提示危化品类别不能为空\n</p>\n<p>\n	[期望]<span>修改页面的必填项危化品类别输入框有相应的信息</span>\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-04-18 16:25:17', 'closed', 'zhongle', '2019-04-23 10:50:45', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2136, 2134, 11, 'Windows-非管理员账号学习课程未记录到个人中心我的课程', 'codeerror', '3', '<p>\n	[步骤]1、登录前台系统\n</p>\n<p>\n	2、点击课程进行学习\n</p>\n<p>\n	3、点击个人中心-我的课程\n</p>\n<p>\n	[结果]没有我的课程在记录课程学习进度\n</p>\n<p>\n	[期望]可以在我的课程记录学习进度\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-04-18 16:33:58', 'zhangbin', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2137, 2135, 11, 'Windows-建议普通用户无法查看企业部门上传课件或者普通用户不显示企业部门上传课件', 'codeerror', '4', '<p>\n	[步骤]1、登录管理员账号建立课程并且上传课件\n</p>\n<p>\n	2、登录普通用户账号进入课程中心\n</p>\n<p>\n	[结果]普通用户可以查看企业部门课件\n</p>\n<p>\n	[期望]建议无法查看企业部门上传课件或者普通用户不显示企业部门上传课件\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-04-18 16:38:30', 'zhangbin', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2138, 2136, 11, '流入管理详情里面的修改页面无法保存成功', 'codeerror', '3', '<p>\n	[步骤]进入流入管理的详情页面，点击编辑按钮，进入到修改页面，正确修改相应的信息，点击保存\n</p>\n<p>\n	[结果]点击保存后页面没有任何反应，无法保存，且没有提示\n</p>\n<p>\n	[期望]点击保存后能够保存修改的内容\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-04-18 16:40:53', 'closed', 'zhongle', '2019-05-10 10:48:15', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2139, 2137, 11, 'Windows-建议可以多部门授权课程', 'codeerror', '4', '<p>\n	[步骤]1、使用管理员账号登录系统\n</p>\n<p>\n	2、在管理中心-课程管理选择授权部门\n</p>\n<p>\n	[结果]只能授权一个部门\n</p>\n<p>\n	[期望]建议可以授权多个部门\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-18 16:44:18', 'closed', 'zhangbin', '2019-04-19 15:05:25', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2140, 2138, 16, '学校--远距离考勤机--拿着班牌从进入学校到达内部远距离考勤的考勤范围，直接出去，远距离考勤机屏幕上显示进校，而孩子在学校外面', 'codeerror', '2', '<p>\n	[步骤]\n</p>\n<p>\n	1.拿着班牌经过学校外面的远距离考勤机进入学校里面，经过学校内部的远距离考勤机，被扫上后，直接出学校。\n</p>\n<p>\n	2.远距离考勤机显示学生入校，但是学生却在外面。\n</p>\n<p>\n	[结果]\n</p>\n学生的处于学校外面，远距离考勤机显示入校<br /><p>\n	[期望]\n</p>\n<p>\n	学生处于学校外面，远距离考勤机就要显示在出校；\n</p>\n<p>\n	</p><p>\n		学生处于学内，远距离考勤机就要显示在入校；\n	</p>\n', 'closed', 'zhangjch', '2019-04-18 17:09:18', 'closed', 'zhangjch', '2019-05-13 15:58:28', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2141, 2140, 11, 'Windows-单位管理部门没有按照所属公司显示', 'codeerror', '3', '<p>\n	[步骤]1、登录后台系统\n</p>\n<p>\n	2、进入单位管理-部门管理\n</p>\n<p>\n	[结果]部门没有按照所属公司显示\n</p>\n<p>\n	[期望]部门按照所属公司显示\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-04-19 13:18:58', 'zhangbin', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2142, 2139, 17, '数据审核，选择几个监测站进行查询，都无法查询到数据', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n数据审核，选择几个监测站进行查询，都无法查询到数据<br /><p>\n	[结果]\n</p>\n<br /><p>\n	[期望]\n</p>\n<br />', 'closed', 'wangsw', '2019-04-19 10:57:07', 'closed', 'wangsw', '2019-04-19 16:15:29', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2143, 2141, 11, 'Windows-部门学员无法删除', 'codeerror', '3', '<p>\n	[步骤]1、登录后台系统\n</p>\n<p>\n	2、进入单位管理-部门管理-学员情况\n</p>\n<p>\n	3、选择常国城点击删除\n</p>\n<p>\n	[结果]系统提示无法删除\n</p>\n<p>\n	[期望]可以删除未激活账号\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-19 13:22:09', 'closed', 'changguocheng', '2019-04-26 14:45:52', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2144, 2142, 11, 'Windows-学员情况列表显示不合理', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在单位管理-部门管理查看学员情况\n</p>\n<p>\n	[结果]1、2页内容显示一致\n</p>\n<p>\n	[期望]<span>1、2页内容显示不一致</span>\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-19 13:39:18', 'closed', 'changguocheng', '2019-04-26 14:46:24', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2145, 2143, 11, 'Windows-后台可以添加前台已经注册的账号', 'codeerror', '2', '<p>\n	[步骤]1、登录前台系统注册账号\n</p>\n<p>\n	2、登录后台系统添加一样手机号的用户\n</p>\n<p>\n	[结果]可以成功添加\n</p>\n<p>\n	[期望]不能添加一样手机号的账号\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-19 13:46:53', 'closed', 'changguocheng', '2019-04-26 14:40:40', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2146, 2144, 11, 'Windows-添加部门时应该可以多次选择同一个企业', 'codeerror', '3', '<p>\n	[步骤]1、登录后台系统\n</p>\n<p>\n	2、在单位管理-部门管理添加部门\n</p>\n<p>\n	[结果]只能添加一次部门就无法再次选择该企业\n</p>\n<p>\n	[期望]应该可以多次选择同一个企业\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-19 14:27:12', 'closed', 'changguocheng', '2019-04-25 11:07:29', NULL, NULL, '0', 'zt');
