INSERT INTO `bug` VALUES (2148, 2147, 11, 'Windows-ie10浏览器无法登录系统', 'codeerror', '2', '<p>\n	[步骤]在ie10浏览器输入系统后台网址\n</p>\n<p>\n	[结果]无法登录\n</p>\n<p>\n	[期望]可以成功登录系统\n</p>\n<p>\n	[环境]win10+ie10+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-04-19 15:17:22', 'chenzm', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2149, 2148, 11, 'Windows-我的成绩开始交卷时间显示不合理', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在个人中心-在线考试查看考试成绩\n</p>\n<p>\n	[结果]开始时间和交卷时间显示不合理\n</p>\n<p>\n	[期望]<span>开始时间和交卷时间显示合理</span>\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-19 16:14:41', 'closed', 'changguocheng', '2019-04-25 11:07:43', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2150, 2149, 11, 'Windows-已经结束的考试建议状态改成已结束', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、进入个人中心-在线考试\n</p>\n<p>\n	[结果]缺少已经结束的考试状态\n</p>\n<p>\n	[期望]已经结束的考试建议状态改成已结束\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'closed', 'changguocheng', '2019-04-19 16:17:16', 'closed', 'changguocheng', '2019-04-25 11:10:02', NULL, NULL, '0', 'zt');
