INSERT INTO `bug` VALUES (2244, 2243, 27, 'UAC生产环境集群崩溃', 'others', '2', '<p>\n	【问题发生时间】\n</p>\n<p>\n	2019.4.28 上午8:00\n</p>\n<p>\n	【问题描述】\n</p>\n2019年4月28日上午8点，由于日志跑满，导致UAC生产环境集群崩溃，系统无法登陆。影响系统：环保二期、化工园区移动APP、危化品全生命周期、智慧消防、导航大厅、一张图、五水共治<br /><p>\n	【解决措施】\n</p>\n详见：http://confluence.asoco.com.cn/x/QQxy<br /><p>\n	<br /></p>\n<br />', 'active', 'liaoxm', '2019-04-28 10:07:31', 'liuh', NULL, NULL, NULL, NULL, '0', 'zt');
