INSERT INTO `bug` VALUES (2328, 2328, 11, '流入管理的新增信息显示在列表的最后一行', 'codeerror', '4', '<p>\n	[步骤]进入流入管理，点击新增流入按钮，打开新增页面，正确的填写好新增的内容，点击保存<br />\n[结果]新增成功，新增的信息列表显示在最后一行<br />\n[期望]新增成功，新增的信息保存在列表信息的第一行<br />\n[环境]win7+Chrome61+1920*1080\n</p>\n<br />', 'closed', 'zhongle', '2019-05-06 14:07:05', 'closed', 'zhongle', '2019-05-10 10:22:14', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2329, 2329, 11, '存量管理的页面的列表大约需要三秒的时间才能显示列表信息', 'codeerror', '3', '<p>\n	[步骤]打开存量管理模块，查看页面内容\n</p>\n<p>\n	[结果]页面的列表内容大约需要三秒的时间才能显示\n</p>\n<p>\n	[期望]页面打开能快速的显示出列表信息内容\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-05-06 14:24:40', 'closed', 'zhongle', '2019-05-10 10:20:25', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2330, 2330, 11, '存量管理的新增页面的总存量输入负数也可以保存成功', 'codeerror', '3', '<p>\n	[步骤]进入存量管理的新增页面，在总存量的输入框中输入负数，点击保存\n</p>\n<p>\n	[结果]总存量输入框输入负数也能保存成功\n</p>\n<p>\n	[期望]输入负数时有相应的提示\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-05-06 14:35:42', 'closed', 'zhongle', '2019-05-10 10:20:03', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2331, 2331, 11, '流出管理的新增信息显示在列表的最后一行', 'codeerror', '3', '<p>\n	[步骤]进入流出管理，点击新增储罐按钮，打开新增页面，正确的填写好新增的内容，点击保存<br />\n[结果]新增成功，新增的信息列表显示在最后一行<br />\n[期望]新增成功，新增的信息保存在列表信息的第一行<br />\n[环境]win7+Chrome61+1920*1080\n</p>\n<p>\n	<br /></p>\n<br />', 'closed', 'zhongle', '2019-05-06 14:48:08', 'closed', 'zhongle', '2019-05-10 10:17:02', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2332, 2332, 11, '统计页面的当前存量分析部分的图形和字体有相互遮挡的地方', 'codeerror', '3', '<p>\n	[步骤]进入统计模块页面，查看页面信息\n</p>\n<p>\n	[结果]统计页面的当前存量分析部分的图形和字体有相互遮挡的地方\n</p>\n<p>\n	[期望]统计页面的当前存量分析部分的图形和字体不相互遮挡\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-05-06 15:28:59', 'closed', 'zhongle', '2019-05-10 10:14:23', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2333, 2333, 11, '商务计划和生产计划的新增页面的计划入库时间没有可供选择的时间也不能手动输入', 'codeerror', '3', '<p>\n	[步骤]进入商务计划和生产计划模块，点击新增\n</p>\n<p>\n	[结果]新增页面的计划入库时间没有可供选择的时间也不能手动输入\n</p>\n<p>\n	[期望]新增页面的计划入库时间有可选择的时间或者能手动输入\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'active', 'zhongle', '2019-05-07 16:24:13', 'bajiaoshan', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2334, 2334, 11, '传感设备管理的新增页面无法保存成功', 'codeerror', '2', '<p>\n	[步骤]进入传感设备管理的新增页面，正确填好新增信息，点击保存\n</p>\n<p>\n	[结果]点击保存按钮后，页面没有反映，无法新增成功\n</p>\n<p>\n	[期望]点击保存按钮后可以新增成功并显示在列表第一行\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'resolved', 'zhongle', '2019-05-07 16:38:01', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2335, 2335, 11, '新增重大危险源管理页面的取消按钮无法回到列表页面', 'codeerror', '3', '<p>\n	[步骤]进入重大危险源管理的新增页面，点击取消按钮\n</p>\n<p>\n	[结果]点击取消按钮后页面无法回到列表页面\n</p>\n<p>\n	[期望]<span>点击取消按钮后页面可回到列表页面</span>\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-05-07 17:00:39', 'closed', 'zhongle', '2019-05-10 11:15:13', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2336, 2336, 11, '商务计划模块和生产计划模块按计划出库时间查询条件查询，系统显示：查询列表异常', 'codeerror', '3', '<p>\n	[步骤]进入商务计划和生产计划模块，选择计划出库时间的查询条件查询\n</p>\n<p>\n	[结果]点击查询后系统显示：查询列表异常\n</p>\n<p>\n	[期望]点击查询后能正常显示查询结果\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'closed', 'zhongle', '2019-05-07 17:08:03', 'closed', 'zhongle', '2019-05-10 11:14:51', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2337, 2337, 11, 'Windows-endTime建议改成一天的结束时间', 'codeerror', '3', '<p>\n	[步骤]打开控制台查看库存管理-入库管理模块日期选择项Headers\n</p>\n<p>\n	[结果]endTime为一天的开始时间\n</p>\n<p>\n	[期望]建议改成一天的结束时间\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-05-08 11:20:07', 'changguocheng', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2338, 2338, 1, 'android-更新进度条显示异常', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n使用小米手机，将APP1.2.1版本更新到1.3.0版本<br /><p>\n	[结果]\n</p>\n更新进度条显示异常<br /><p>\n	[期望]\n</p>\n正常显示更新进度条', 'active', 'yangzixi', '2019-05-08 15:56:04', 'liangy', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2339, 2339, 1, '报警推送中订阅失败后仍然会选择订阅项', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n再报警推送中订阅相关推送，再订阅失败的情况下，重新点击订阅，会出现订阅项仍然呗选择的情况<br /><p>\n	[结果]\n</p>\n<img src=\"{2129.png}\" alt=\"\" />该情况再订阅用户、选择类别中都有<br /><p>\n	[期望]\n</p>\n应该情况选择', 'closed', 'yangzixi', '2019-05-08 16:16:58', 'closed', 'yangzixi', '2019-05-10 16:21:38', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2340, 2397, 11, 'Windows-仓库车辆信息提示后台服务错误', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在实时监控查看车辆信息\n</p>\n<p>\n	[结果]系统提示后台服务错误\n</p>\n<p>\n	[期望]没有数据提示暂无数据\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-13 10:36:36', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2341, 2340, 11, 'Windows-评价无效', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在日常管理-应急事件管理评价无效\n</p>\n<p>\n	[结果]评价无效\n</p>\n<p>\n	[期望]<span>评价有效</span>\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-08 19:46:43', 'bajiaoshan', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2342, 2341, 11, 'Windows-人员照片无法显示', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、查看人员信息\n</p>\n<p>\n	[结果]人员照片无法显示\n</p>\n<p>\n	[期望]人员照片可以显示\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-08 19:48:46', 'bajiaoshan', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2343, 2342, 1, 'iOS-点击报警消息的详情查看未跳转到对应的未处理界面', 'codeerror', '2', '<p>\n	[步骤]\n</p>\n再消息列表点击报警推送的消息，然后点击查看详情<br /><p>\n	[结果]\n</p>\n跳转到的是一月份的已处理界面<br /><p>\n	[期望]\n</p>\n<p>\n	应该是跳转到当前月份的未处理界面\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：iPhone&nbsp;xr\n</p>', 'closed', 'yangzixi', '2019-05-09 08:45:49', 'closed', 'yangzixi', '2019-05-10 09:28:53', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2344, 2343, 1, 'iOS-报警推送无弹窗', 'codeerror', '3', '<p>\n	[步骤]\n</p>\niOS登陆ceshi&nbsp;账户，然后使用星磐报警组件推送报警信息（已订阅相关报警）<br /><p>\n	[结果]\n</p>\n无弹窗提示，android有弹窗。点击消息列表可以看到有未读消息<br /><p>\n	[期望]\n</p>\n<p>\n	正常弹窗提示\n</p>\n<p>\n	测试环境：iPhone&nbsp;xr\n</p>', 'closed', 'yangzixi', '2019-05-09 08:56:13', 'closed', 'yangzixi', '2019-05-10 09:08:38', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2345, 2344, 1, 'android-查看报警推送的点位出现的地图位置不正确', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n点击报警的点位详情，查看对应点位<br /><p>\n	[结果]\n</p>\n地图显示为手机所在的未知，不是点位所在的位置<br /><p>\n	[期望]\n</p>\n<p>\n	应该显示点位所在的未知\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：iPhone&nbsp;xr\n</p>', 'closed', 'yangzixi', '2019-05-09 08:58:06', 'closed', 'yangzixi', '2019-05-10 09:33:29', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2346, 2345, 1, 'android-报警中的标准值显示异常', 'codeerror', '2', '<p>\n	[步骤]\n</p>\n查看未处理的报警，android显示的标准值和iOS的不一样<br /><p>\n	[结果]\n</p>\n例如：2019-05-05 15:33:21的人防站报警，android显示为null-null&nbsp; ，iOS显示为0-100。<br /><p>\n	[期望]\n</p>\n<p>\n	正常显示标准值\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：&nbsp;iPhone&nbsp;xr\n</p>', 'closed', 'yangzixi', '2019-05-09 09:05:21', 'closed', 'yangzixi', '2019-05-14 09:40:05', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2347, 2346, 1, '报警未处理列表排序未按时间排序', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n打开报警推送的详情，查看未处理列表<br /><p>\n	[结果]\n</p>\n列表中显示的报警信息排序未按时间排序<br /><p>\n	[期望]\n</p>\n<p>\n	应该按时间进行倒序排序\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：iPhone&nbsp;xr 、 小米5s\n</p>', 'closed', 'yangzixi', '2019-05-09 09:07:00', 'closed', 'yangzixi', '2019-05-14 09:39:00', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2348, 2347, 1, 'iOS-报警列表中显示的数据和单位过于靠近', 'codeerror', '4', '<p>\n	[步骤]\n</p>\n<img src=\"{2132.png}\" alt=\"\" /><br /><p>\n	[结果]\n</p>\n如图所示，数据和单位过于靠近，<br /><p>\n	[期望]\n</p>\n<p>\n	<span>应该稍微分开</span>\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：iPhone&nbsp;xr\n</p>', 'closed', 'yangzixi', '2019-05-09 09:10:02', 'closed', 'yangzixi', '2019-05-10 09:28:59', NULL, NULL, '0', 'zt');
