INSERT INTO `bug` VALUES (2350, 2349, 1, '点击报警列表的年份处无年份选择菜单按钮', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n<p>\n	根据原型图描述，年份处应该有年份的选择菜单按钮\n</p>\n<p>\n	<img src=\"{2135.png}\" alt=\"\" /></p>\n<p>\n	[结果]\n</p>\n<br /><p>\n	[期望]\n</p>\n如原型图所示<img src=\"{2136.png}\" alt=\"\" />', 'active', 'yangzixi', '2019-05-09 09:21:56', 'lic', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2351, 2350, 11, 'Windows-无法添加精选视频', 'codeerror', '3', '<p>\n	[步骤]后台无法添加精选视频\n</p>\n<p>\n	[结果]<span>后台无法添加精选视频</span>\n</p>\n<p>\n	[期望]<span>后台可以添加精选视频</span>\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-05-09 09:35:24', 'changguocheng', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2352, 2351, 1, 'iOS和android的3月份报警列表显示数据不一样', 'codeerror', '2', '<p>\n	[步骤]\n</p>\n<div>\n	<img src=\"{2137.png}\" alt=\"\" /><img src=\"{2138.png}\" alt=\"\" /><br /></div>\n<br /><p>\n	[结果]\n</p>\n<br /><p>\n	[期望]\n</p>\n<p>\n	应该显示一致\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：iPhone&nbsp;xr 、&nbsp;小米5s\n</p>', 'closed', 'yangzixi', '2019-05-09 09:35:41', 'closed', 'yangzixi', '2019-05-10 09:34:03', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2353, 2352, 1, 'iOS-未处理的报警上面不应显示日期', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n<img src=\"{2140.png}\" alt=\"\" /><br /><p>\n	[结果]\n</p>\n<br /><p>\n	[期望]\n</p>\n已处理的报警才应该显示信息', 'closed', 'yangzixi', '2019-05-09 09:38:17', 'closed', 'yangzixi', '2019-05-10 09:30:27', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2354, 2353, 11, '新增预案页面的右上角缺少返回上一级的按钮', 'codeerror', '4', '<p>\n	[步骤]进入新增预案模块，点击专项应急预案按钮，查看页面信息\n</p>\n<p>\n	[结果]页面没有返回上一级的按钮\n</p>\n<p>\n	[期望]页面右上角有返回上一级的按钮\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'active', 'zhongle', '2019-05-09 09:46:20', 'bajiaoshan', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2355, 2354, 1, 'iOS-从站点的超标查询跳转到报警列表的日期不正确', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n点击站点超标查询的操作按钮，然后跳转到报警列表。<br /><p>\n	[结果]\n</p>\n跳转到的是1月份的报警列表，不是点击的超标报警的时间对应的月份<br /><p>\n	[期望]\n</p>\n应该和超标值显示的月份对应上', 'closed', 'yangzixi', '2019-05-09 09:55:05', 'closed', 'yangzixi', '2019-05-10 09:29:38', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2356, 2355, 1, 'android-从报警列表的点位详情中查看超标查询再进入报警列表后返回列表日期选择后无反应', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n<p>\n	1.再报警列表中点击点位详情，查看点位的超标查询界面\n</p>\n<p>\n	2.在点位的超标查询界面点击操作的圆点，进入报警列表\n</p>\n<p>\n	3.返回到最初的报警列表界面\n</p>\n<p>\n	4.点击报警列表中的月份\n</p>\n<p>\n	[结果]\n</p>\n点击月份后无反应<br /><p>\n	[期望]\n</p>\n<p>\n	正常显示月份数据\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：小米5s\n</p>', 'closed', 'yangzixi', '2019-05-09 09:58:45', 'closed', 'yangzixi', '2019-05-10 09:34:28', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2357, 2356, 1, '点击应用中的超标处理默认进入界面应该是当前月份未处理界面', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n点击应用模块中的超标处理模块<br /><p>\n	[结果]\n</p>\n<p>\n	android的默认进入界面为当前月份的已处理界面\n</p>\n<p>\n	iOS无该模块\n</p>\n<p>\n	[期望]\n</p>\n进入界面应该是当前月份未处理界面，iOS应添加该模块<br />', 'closed', 'yangzixi', '2019-05-09 10:16:36', 'closed', 'yangzixi', '2019-05-10 15:32:49', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2358, 2357, 1, 'iOS-报警列表下拉后无限循环', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n打开报警列表，查看5月份未处理的数据，然后下拉查看<br /><p>\n	[结果]\n</p>\n多次下拉后，发现数据无法拉到底。android可以拉到底<br /><p>\n	[期望]\n</p>\n<p>\n	正常显示数据\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：iPhone&nbsp;xr\n</p>', 'closed', 'yangzixi', '2019-05-09 10:22:28', 'closed', 'yangzixi', '2019-05-10 09:28:39', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2359, 2358, 11, '新增预案的新增信息保存之后系统提示保存成功，但是列表中没有显示', 'codeerror', '3', '<p>\n	[步骤]进入新增预案模块，填写新增信息后点击保存按钮\n</p>\n<p>\n	[结果]点击保存后系统提示保存成功，但是列表中没有新增的信息\n</p>\n<p>\n	[期望]<span>点击保存后系统提示保存成功，列表中显示新增的信息</span>\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'active', 'zhongle', '2019-05-09 10:24:14', 'bajiaoshan', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2360, 2359, 11, 'Windows-返回首页实时数据消失', 'codeerror', '3', '<p>\n	[步骤]1、进入系统首页\n</p>\n<p>\n	2、点击登录，成功跳转登录页面后点击返回\n</p>\n<p>\n	[结果]首页实时数据消失\n</p>\n<p>\n	[期望]<span>首页实时数据不会消失</span>\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-05-09 15:34:21', 'changguocheng', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2361, 2360, 11, 'Windows-建议订单查询增加模糊搜索', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在订单管理-我的订单模块搜索订单编号20190505\n</p>\n<p>\n	[结果]没有数据\n</p>\n<p>\n	[期望]显示订单编号包含20190505的项\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-09 16:19:26', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2362, 2361, 11, '管道管理的新增页面的在地图上标注的重置功能没有实现', 'codeerror', '3', '<p>\n	[步骤]进入管道管理页面，点击新增，进入新增页面，点击在地图上标注的按钮，标注后点击重置按钮\n</p>\n<p>\n	[结果]刚刚标注管道线路的无法重置成功\n</p>\n<p>\n	[期望]刚标注的管道线路可重置成功\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'resolved', 'zhongle', '2019-05-09 16:41:31', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2363, 2362, 11, 'Windows-消息推送第1、2页列表信息显示一致', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、进入消息推送模块\n</p>\n<p>\n	[结果]第1、2页列表信息显示一致\n</p>\n<p>\n	[期望]第1、2页列表信息显示不一致\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-05-09 17:06:59', 'changguocheng', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2364, 2363, 11, 'Windows-题库类别页面建议增加下拉显示功能', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、查看题库类别模块\n</p>\n<p>\n	[结果]内容多时无法完整显示全部内容\n</p>\n<p>\n	[期望]建议增加下拉显示功能\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-05-09 17:24:32', 'changguocheng', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2365, 2364, 11, 'Windows-搜索后建议增加查看查看全文功能', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、搜索法律法规\n</p>\n<p>\n	[结果]法律法规内容无法查看\n</p>\n<p>\n	[期望]可以搜索后点击标题查看法律法规内容\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'resolved', 'changguocheng', '2019-05-09 17:30:01', 'changguocheng', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2366, 2365, 11, '没有填写今日作战编程的新增页面的必填项，点击确定，确定按钮一直处于加载的状态', 'codeerror', '3', '<p>\n	[步骤]进入今日作战编程新增页面，不填写或者不填写完必填项，点击新增页面的必填项\n</p>\n<p>\n	[结果]系统提示“红色*部分为必填项”，但是新增页面的确定按钮一直处于加载的状态，关闭新增页面之后再次打开新增页面，确定按钮还是处于加载状态，需要刷新之后新增页面打开才能正常\n</p>\n<p>\n	[期望]<span>系统提示“红色*部分为必填项</span><span>”，确定按钮能正常，关闭新增页面后再次打开可以正常新增</span>\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'resolved', 'zhongle', '2019-05-10 09:18:53', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2367, 2366, 11, 'Windows-重选承运方提示后台服务错误', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、拒绝接单后重选承运方\n</p>\n<p>\n	[结果]系统提示后台服务错误\n</p>\n<p>\n	[期望]可以成功重选承运方\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 09:23:18', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2368, 2367, 11, 'Windows-新增罐体无法关联挂车', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在企业管理-罐体管理新增罐体\n</p>\n<p>\n	3、在关联挂车搜索挂\n</p>\n<p>\n	[结果]显示暂无数据\n</p>\n<p>\n	[期望]车辆管理模块添加了挂车，应该是有数据\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 09:31:58', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2369, 2368, 11, '今日作战编程新增页面的必填项不填写完全也能新增成功', 'codeerror', '3', '<p>\n	[步骤]进入今日作战编程的新增页面，新增页面的必填项没有填写完，点击确定按钮\n</p>\n<p>\n	[结果]新增信息能保存成功\n</p>\n<p>\n	[期望]新增页面的必填项填写完才能保存成功\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'resolved', 'zhongle', '2019-05-10 09:36:43', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2370, 2369, 1, '地图上的微观站iOS和android的AQI数值显示不一致', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n再地图上选择微观站图层，查看显示的AQI指数<br /><p>\n	[结果]\n</p>\n<p>\n	<span>android</span>显示的数值比<span>iOS</span>大1（推测为四舍五入的方法不一致导致）\n</p>\n<p>\n	ps：&nbsp;除AQI之外，其他类型如so2、no2也有差异。\n</p>\n<p>\n	[期望]\n</p>\n<p>\n	数值显示显示相同\n</p>\n<p>\n	测试环境：iPhone&nbsp;xr&nbsp; 、小米5s\n</p>', 'closed', 'yangzixi', '2019-05-10 09:40:43', 'closed', 'yangzixi', '2019-05-13 09:11:47', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2371, 2370, 11, 'Windows-查询5月10号到11号装货运单无数据', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在运单管理-我的运单按照装货时间查询运单\n</p>\n<p>\n	[结果]查询5月10号到11号装货运单无数据\n</p>\n<p>\n	[期望]有数据时应该显示数据\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 09:42:20', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2372, 2371, 11, '没有填写车辆管理的新增页面的必填项，点击确定，确定按钮一直处于加载的状态', 'codeerror', '3', '<p>\n	[步骤]进入车辆管理新增页面，不填写或者不填写完必填项，点击新增页面的必填项<br />\n[结果]系统提示“请填写车辆名称”，但是新增页面的确定按钮一直处于加载的状态，关闭新增页面之后再次打开新增页面，确定按钮还是处于加载状态，需要刷新之后新增页面打开才能正常<br />\n[期望]系统提示“请填写车辆名称”，确定按钮能正常，关闭新增页面后再次打开可以正常新增<br />\n[环境]win7+Chrome61+1920*1080\n</p>\n<br />', 'resolved', 'zhongle', '2019-05-10 09:46:07', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2373, 2372, 11, 'Windows-对结算理模块账单号搜索建议增加模糊搜索', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、结算管理-对账管理选择对账单号搜索2019\n</p>\n<p>\n	[结果]显示没有数据\n</p>\n<p>\n	[期望]建议增加模糊搜索\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 09:50:06', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2374, 2373, 11, 'Windows-产品管理查询4月3号到4号没有数据', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在产品管理-我的产品选择4月3号到4号时间查询\n</p>\n<p>\n	[结果]系统显示暂无数据\n</p>\n<p>\n	[期望]有数据时应该显示有的数据\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 09:57:48', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2375, 2374, 11, ' 没有填写人员管理的新增页面的必填项，点击确定，确定按钮一直处于加载的状态', 'codeerror', '3', '<p>\n	[[步骤]进入人员管理新增页面，不填写或者不填写完必填项，点击新增页面的必填项<br />\n[结果]系统提示“请填写人员名称”，但是新增页面的确定按钮一直处于加载的状态，关闭新增页面之后再次打开新增页面，确定按钮还是处于加载状态，需要刷新之后新增页面打开才能正常<br />\n[期望]系统提示“请填写人员名称”，确定按钮能正常，关闭新增页面后再次打开可以正常新增<br />\n[环境]win7+Chrome61+1920*1080\n</p>\n<p>\n	<br /></p>', 'resolved', 'zhongle', '2019-05-10 10:06:46', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2376, 2375, 11, '快速点击存量管理的新增页面的保存按钮可新增两条相同的信息', 'codeerror', '4', '<p>\n	[步骤]进入存量管理的新增页面，正确的填写好新增的内容，快速点击保存按钮\n</p>\n<p>\n	[结果]可保存两条及两条以上相同的信息\n</p>\n<p>\n	[期望]只保存一条信息，有相同的信息时会有相应的提示\n</p>\n<p>\n	[环境]win7+Chrome61+1920*1080\n</p>', 'resolved', 'zhongle', '2019-05-10 10:58:22', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2377, 2376, 11, '快速点击企业管理的新增页面的保存按钮可新增两条相同的信息', 'codeerror', '4', '<p>\n	[步骤]进入企业管理的新增页面，正确的填写好新增的内容，快速点击保存按钮<br />\n[结果]可保存两条及两条以上相同的信息<br />\n[期望]只保存一条信息，有相同的信息时会有相应的提示<br />\n[环境]win7+Chrome61+1920*1080<br /></p>\n<br />', 'resolved', 'zhongle', '2019-05-10 11:01:28', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2378, 2377, 11, '快速点击流出管理的新增页面的保存按钮可新增两条相同的信息', 'codeerror', '4', '<p>\n	[步骤]进入流出管理的新增页面，正确的填写好新增的内容，快速点击保存按钮<br />\n[结果]可保存两条及两条以上相同的信息<br />\n[期望]只保存一条信息，有相同的信息时会有相应的提示<br />\n[环境]win7+Chrome61+1920*1080\n</p>\n<p>\n	<br /></p>\n<br />', 'resolved', 'zhongle', '2019-05-10 11:06:17', 'zhongle', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2379, 2378, 1, 'android-微观站的SO2显示了小数点iOS没有显示', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n打开微观站，选择SO2，查看地图显示<br /><p>\n	[结果]\n</p>\n小于10的数据，显示了一位小数点<br /><p>\n	[期望]\n</p>\n<p>\n	应该和iOS一致，不显示小数点\n</p>\n<p>\n	<br /></p>\n<p>\n	测试环境：小米5s\n</p>', 'closed', 'yangzixi', '2019-05-10 11:07:27', 'closed', 'yangzixi', '2019-05-10 15:31:53', NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2380, 2379, 11, 'Windows-省外订单多运单派车，派车页面信息没有重置', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、我的订单页面派车\n</p>\n<p>\n	[结果]多订单派车时，派车页面信息没有重置\n</p>\n<p>\n	[期望]每次派车派车页面信息重置\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 13:32:36', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2381, 2380, 11, 'Windows-商品名称搜索无效', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在结算管理-对账管理-有争议账单商品名称输入框输入111，点击搜索\n</p>\n<p>\n	[结果]列表显示全部信息\n</p>\n<p>\n	[期望]列表显示符合搜索条件的项\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 13:46:01', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2382, 2381, 11, 'Windows-有争议省外运单没有显示运单号并且省内运单运单号显示不完整', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在结果管理-账单管理-有争议运单查看运单信息\n</p>\n<p>\n	[结果]有争议省外运单没有显示运单号并且省内运单运单号显示不完整\n</p>\n<p>\n	[期望]有争议省外运单显示运单号并且省内运单运单号显示完整\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 13:50:04', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2383, 2382, 11, 'Windows-运单编号搜索无效', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在结算管理-账单管理-有争议运单运单输入框输入201905050001\n</p>\n<p>\n	[结果]列表显示全部项信息\n</p>\n<p>\n	[期望]列表只显示符合搜索条件的项信息\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 13:52:13', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2384, 2383, 11, 'Windows-有争议运单审核和查看凭证无效', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在结算管理-对账管理-有争议运单点击审核和查看凭证\n</p>\n<p>\n	[结果]系统无反应\n</p>\n<p>\n	[期望]审核有效并且可以查看凭证，如果没有凭证应给友好性提示\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 13:54:20', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2385, 2384, 11, 'Windows-承运方搜索无效', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在结算管理-发票管理-已开票承运方输入框输入<span style=\"color:#606266;font-family:Avenir, Helvetica, Arial, sans-serif;font-size:12px;background-color:#F5F7FA;\">濮阳市盛和物流货运有限公司，点击搜索</span> \n</p>\n<p>\n	[结果]系统显示暂无数据\n</p>\n<p>\n	[期望]系统显示符合搜索条件的项\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 14:01:01', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2386, 2385, 11, 'Windows-未开票切换到已开票搜索内容没有重置', 'codeerror', '3', '<p>\n	[步骤]1、登录系统\n</p>\n<p>\n	2、在结算管理-发票管理未开票<span>对账单编号输入1111，切换到已开票</span>\n</p>\n<p>\n	[结果]输入框内容没有重置\n</p>\n<p>\n	[期望]输入框内容重置\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 14:18:41', 'ziding', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2387, 2386, 11, 'Windows-仓库管理-我的仓库 模块按“仓库名称”条件查询异常', 'codeerror', '3', '<p>\n	[步骤]\n</p>\n<p>\n	1、登录系统\n</p>\n<p>\n	2、打开【仓库管理】-【我的仓库】模块\n</p>\n<p>\n	3、在仓库名称搜索框输入有数据的仓库名称，点击【查询】\n</p>\n<p>\n	[结果]\n</p>\n<p>\n	查询结果显示“暂无数据”\n</p>\n<p>\n	[期望]\n</p>\n<p>\n	能正常返回查询结果\n</p>\n<p>\n	[环境]win10+chrome70+分辨率1920*1080\n</p>', 'active', 'changguocheng', '2019-05-10 15:03:13', 'huangr', NULL, NULL, NULL, NULL, '0', 'zt');
INSERT INTO `bug` VALUES (2388, 2387, 16, '直接从“我的空间”登录上去接收不到消息通知', 'codeerror', '3', '<p>\n	[步骤]对于班级通知、校级通知和紧急通知，直接从“我的空间”登录上去接收不到信息。\n</p>\n<p>\n	正常步骤：从webos-门户大屏-我的空间，这样登录上去才可以接收到，但是这样不符合易用性使用逻辑。\n</p>\n<br /><p>\n	[结果]\n</p>\n<br /><p>\n	[期望]希望从任意界面登录上来都可以接收到通知\n</p>\n<br />', 'resolved', 'liangzy', '2019-05-10 15:06:58', 'liangzy', NULL, NULL, NULL, NULL, '0', 'zt');
